css:
@-moz-document url-prefix("http://i.doit.im/"), url-prefix("http://doit.im/") {

#task_group {
    margin-bottom: 0;
    margin-top: 0;
}
#task_paper {
    margin-top: 6px;
    padding: 0 12px;
}
.quickadd-bar .quick {
    padding: 24px 0 7px;
    height: 28px;
}
#header {
    padding: 0;
}
input.task-quick-add {
    padding: 6px 100px 6px 12px;
}
.task-group .groups .group {
    margin-top: 8px;
}
.toolbar {
    height: 30px;
}
#groupby {
    margin-top: 5px;
}
#sidebar h3 {
    margin: 0;
}
.sidebar li {
    height: 26px;
    line-height: 26px;
}
.sidebar .side-h3,
#sidebar_right .side-title {
    margin: 2px 0 0;
}
#goal_root {
    display: none;
}
#context_root {
    display: none;
}
#context_list {
    display: none;
}
.filter {
    display: none;
}
#sidebar h3 {
    display: none;
}
#sidebar ul {
    padding: 5px 0;
}
.sidebar li .side-item {
    padding-left: 0;
}
#task_container > div {
    height: auto !important;
}
.task-attr,
.project-attr {
    margin-bottom: 14px;
}
#project_info {
    margin-bottom: 0;
}
.project-op,
.task-op,
.project-op,
.goal-op {
    margin-top: 0;
    margin-bottom: 0;
}
#project_info .detail .evernote {
    display: none;
}
li.task .title .project {
    margin: 9px 10px 0;
    padding: 2px 5px 1px;
    border-radius: 2px;
    border: 1px solid #ccc;
    background: transparent;
    color: #666 !important
}
}
