 * This user style is licensed under Creative Commons CC-BY-SA 4.0.
 * In a nutshell, it means you may copy, modify, or distribute the work
 * so long as the author (zlg) is visibly credited and your derivative is
 * shared under a similarly liberating license.
 *
 * This style is merely cosmetic. It gets rid of the right bar, ads, and the
 * "See also" block, allowing the reader to focus on the stories and comments.
 */

@-moz-document domain("slashdot.org") {

.container .main-wrap.has-rail-right > .main-content,
div#comments.a2commentwrap
{
	margin: 1em !important;
}

aside.view_mode,
.row.top-space,
.scw-horizontal.stackcommerce-widget,
#announcement,
#slashboxes
{
	display: none !important;
}

#fhft,
#ft
{
	min-width: unset !important;
}

.bq {
	margin-right: 0;
}

} /* end domain("slashdot.org") */
