css:
@-moz-document domain("mangapark.me"), domain("mangapark.com"), domain("mangapark.net") {

/*Main*/
header.cm nav li a { color: #d2d2d2; }
header.cm nav li.on a { background: #060606; }
header.cm .search input  { border: 1px solid #fff; }
header.cm nav li a:hover { color: #2f2f2f; background: #cacaca; }

body { background: #1b1b1b; color: #b5b5b5;}

section.home aside { background: #000000; }
section.switch { background: #131313; }
section.home h2 { color: #4e4e4e; }
section.home article .ls1 .item.hot h3 a { color: #d61e7c; }
section.home article .ls1 .item.hot h3 a:hover { color: #ff0082; }
section.home article .ls1 .item li.new, section.home article .ls1 .item li.new a:hover{ color: #31ff00; }
section .hd a.more { color: #404040; }
section .hd a.more:hover { color: #ffffff; }
section.switch .thm-list li a.thm-effect { border: 1px solid #353535; background: #128c00; }
section.home .hd li.lst2 a:hover { color: #ffffff; }
section.head-info .content { background: #131313; }

.ui-autocomplete-input { background: #131313; }

a:visited { color: #949494; }
a:link { color: #949494; }
a:hover { color: #ffffff }


/* Search */
section.search .filter { background: #131313; }
section.search .filter .search input, section.search .filter .search select { background: #545454; }
section.search .filter .search-submit { background: #545454; }
section.search .filter .search { background: #545454; }

.manga-list .item .info { background: #080808; }
.manga-list .item h3:hover { background-color: #080808 }

/* Latest */
section.latest .genres { background: #060606; }

/* Genres */
section.genre .genres { background: #080808; }

/* Manga Info */
.book-list h3 { background: #060606; }
.book-list .chapter li.selected { background-color: #000000; }
.book-list .chapter li:hover { background-color: #484848; }
.book-list .stream { border: 1px solid #505050; }

/* Bookmark */
section.hd h1, section .hd h2, section .hd h3, section .hd h4 { color: #ffffff; }
section.ucp .menu li { border: 1px solid #0e0e0e; background: #484848; }
section.ucp .menu li.on { background-color: #8e8e8e; border-color: #0e0e0e; }
section.ucp .bm .items p { background: #080808; }
section.ucp .bm .items a.new:hover { color: #00da00; }
section.ucp .bm .items table h3.hover { background: rgba(0, 0, 0, 0); }
section.ucp .bm .items table li.hover { background: rgba(0, 0, 0, 0); }
section.ucp .bm .items p a.on:hover { color: #e8b904; }
section.ucp .bm .folders li.on a:hover { color: #e8b904;}


/* All Bookmarks */
section.ucp .bm .items thead { background: #080808; }
section.ucp .bm .items tbody tr:hover { background: #484848; }

/* Account Settings */
section.ucp .my h2 { border-bottom: 1px dashed #ffffff; }
section.ucp .my .tbl { background: #080808; }
section.ucp .submenu li { border: 1px solid #0e0e0e; background: #484848; }
section.ucp .submenu li.on { border: 1px solid #ffffff; }
section.ucp .menu { border-bottom: 3px solid #dadada; }

/* Donate */
.message { border: 1px solid #232323; background: #0a0a0a; }
section.ucp .dn thead { background: #080808; }
section.ucp .dn tr { border-top: 1px solid #929292; }
section.ucp .dn tbody tr:hover { background: #484848; }

/* New Site (beta) */
#anyacg { background: #000000; }
#anyacg .title { color: #003555; }
#anyacg a:hover { color: #0084d4; }
#anyacg .volcha:hover { color: #cacaca; }

}
