css:
@-moz-document domain("www.hackerrank.com") {
html {
  background-color: #073642;
}
body {
  background-color: #002b36;
}
body div {
  background-color: #073642;
}
body div nav.page-header {
  background-color: #002b36;
}
body div nav.page-header div {
  background-color: #002b36;
}
body div div.content-header {
  background-color: #073642;
}
body div div.container {
  color: #839496;
  background-color: #073642;
}
body div div.container div.fw.border-bottom.inline-block.psB.pjT span {
  color: #b58900;
}
body div div.container div.fw.border-bottom.inline-block.psB.pjT span.txt-alt-grey-light {
  color: #839496;
}
body div div.container .txt-navy {
  color: #93a1a1;
}
body div div.container .txt-alt-grey {
  color: #839496;
}
body div div.container a.track-tile {
  background-color: #002b36;
}
body div div.container a.track-tile div {
  background-color: #002b36;
}
body div div.container a.track-tile:hover {
  border-color: #859900;
}
body div div.container a.track-tile:hover .txt-navy {
  color: #859900;
}
body div div.container.challenge-container ul.chapter-cards {
  background-color: #073642;
}
body div div.container.challenge-container ul.chapter-cards li.current {
  color: #268bd2;
  border-color: #268bd2;
}
body div div.container.challenge-container ul.chapter-cards li {
  color: #586e75;
}
body div div.container.challenge-container div.challenges-filter-image-outter {
  border: 0;
}
body div div.container.challenge-container div.individual-challenge-card {
  background-color: #002b36;
}
body div div.container.challenge-container div.individual-challenge-card div {
  background-color: #002b36;
}
body div div.container.challenge-container div.individual-challenge-card div.completed-indicator {
  background-color: #859900;
}
body div div.container.challenge-container div.individual-challenge-card h4.is-solved > a {
  color: #93a1a1;
}
body div div.container.challenge-container div.individual-challenge-card h4 > a {
  color: #859900;
}
body div div.container.challenge-container div.individual-challenge-card a.btn {
  background-image: linear-gradient(to bottom, #f2eee0, #eae2ca);
}
body div div.container.challenge-container div.individual-challenge-card a.btn.btn-primary {
  background-image: linear-gradient(to bottom, #92a800, #788a00);
  border: #859900;
  color: #eee8d5;
}
body div div.container.challenge-container div.pagination li.page-item a,
body div div.container.challenge-container div.pagination li.next-page a,
body div div.container.challenge-container div.pagination li.last-page a,
body div div.container.challenge-container div.pagination li.first-page a,
body div div.container.challenge-container div.pagination li.prev-page a {
  background-image: linear-gradient(to bottom, #f2eee0, #eae2ca);
}
body div div.container.challenge-container div.pagination li.page-item.active a {
  background-image: linear-gradient(to bottom, #92a800, #788a00);
  border-color: #859900;
}
body div div.main_content div.challenge-header h2 {
  color: #859900;
}
body div div.main_content div.challenge-header ul.nav {
  background-color: #002b36;
  border-color: #dc322f;
}
body div div.main_content div.challenge-header ul.nav li a {
  color: #78b9e6;
}
body div div.main_content div.challenge-header ul.nav li a:hover {
  color: #268bd2;
}
body div div.main_content div.challenge-header ul.nav li.active > a {
  color: #268bd2;
  background-color: #073642;
  border-color: #dc322f;
  border-bottom-color: #073642;
}
body div div.main_content div.challenge-body aside.challenge-sidebar i.icon-star {
  color: #b58900;
}
body div div.main_content div.challenge-body div.challenge-content pre {
  background-color: #002b36;
}
body div div.main_content div.challenge-body div.challenge-content pre span.n,
body div div.main_content div.challenge-body div.challenge-content pre span.p,
body div div.main_content div.challenge-body div.challenge-content pre span.o,
body div div.main_content div.challenge-body div.challenge-content pre span.err {
  color: #93a1a1;
}
body div div.main_content div.challenge-body div.challenge-content pre span.mi {
  color: #2aa198;
}
body div div.main_content div.challenge-body div.challenge-content pre span.s {
  color: #dc322f;
}
body div div.main_content div.challenge-body div.challenge-request div.grey-header {
  background-color: #002b36;
}
body div div.main_content div.challenge-body div.challenge-request div.grey-header div {
  background-color: #002b36;
}
body div div.main_content div.challenge-body div.challenge-request div.codeshell-footer {
  background-color: #073642;
}
body div div.main_content div.challenge-body div.challenge-request div.codeshell-footer div {
  background-color: #073642;
}
body div div.main_content div.challenge-copyright {
  color: #fdf6e3;
}
body footer.page_footer {
  color: #839496;
  background-color: #002b36;
}
body footer.page_footer div {
  background-color: #002b36;
}

}
