css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("9gag.com") {
  .nav-menu > ul li{float: none}
  .flex.menu li:nth-child(8){visibility: hidden;height: 0}
  .nav-menu{margin: auto}
  header#top-nav .nav-wrap{height: 100vh; position: static;}
  header#top-nav{width: 92px; min-width: 0px;}
  .flex.menu{height: auto; float: left;margin:0;}
  .nav-menu .primary{margin: auto}
  header#top-nav a.logo{width: 92px}
  .function-wrap{float: left; position: static;padding: 0 31px;width: 30px; margin-top:69px;}
  .user-function, .visitor-function{padding: 0; width: 30px}
  .user-function .avatar{left:0; margin: 10px 0px 10px 0px}
  .user-function .upload, .visitor-function .upload{margin-right:-31px;width:92px;}
  .upload .btn-primary{padding: 0 0 0 30px;margin:0; width: 92px; box-sizing: border-box; border-radius: 0px;}
  .badge-sticky-subnav-static{width:92px; position: fixed;}
  #container{margin: 0 400px;}
  .popup-menu.search,.notification-menu,.popup-menu.user{top:5px; left: 97px;right:auto;}
  body{background: #111111}
  section#list-view-2 h2 a{color: white}
  section.block-feature-cover a{color: white}
  section#list-view-2 article{width: 500px;}
  section#individual-post header h2{color: white}
  .post-afterbar-a.in-post-top{border: solid #111111 5px; background-color: #111111}
  .horizontal-vote a.up span{background-color: #111111}
  article.post-page .post-container a img,article.post-page .post-container video{width: 738px !important;}
  article.post-page .post-container .gif-post, article.post-page .post-container .image-post, article.post-page .post-container .nsfw-post, article.post-page .post-container .video-post, article.post-page .post-container .youtube-post {width: 738px;}
  section.post-comment{border: solid #111111 5px; background-color: #111111;width: 728px;}
  .CS3{background-color: #111111}
  .CS3 .comment-entry .payload .username,.CS3 .comment-entry .payload .content,.CS3 .tab-bar h3,.CS3 .tab-bar ul.tab li.active a{color: white;}
  .comment-box .payload .textarea-container textarea.focus{background-color: #111111; color: white;}
  .post-afterbar-a.in-post-bot.full-width .btn-share{width: 44px; min-width: 0px; color:rgba(0,0,0,0);transition: all 1s;}
  .post-afterbar-a.in-post-bot.full-width .btn-share:hover {width: 300px; min-width: 0px; color:white;}
  .post-afterbar-a .btn-share{width: 34px; color: rgba(0,0,0,0);padding: 0;}
}
