css:
@-moz-document domain("bilibili.com") {
.v-title h1 {
    height: auto !important;
    white-space: normal !important;
}

.v-wrap .video-info {
    height: auto !important;
    box-sizing: content-box;
    padding-bottom: 15px;
}

.v-wrap .up-info {
    height: auto !important;
}

#viewbox_report h1 {
    height: auto !important;
    white-space: normal !important;
}

.dynamic-m .dyn_list_wrapper .dyn_list li .r .info a {
    white-space: normal !important;
}
.dynamic-m .dyn_list_wrapper .dyn_list li .r .info {
    height: auto !important;
}

.up-info .up-info_right .desc {
    height: auto;
    white-space: normal;
    margin-bottom: 4px;
}

.title-length-limit {
    max-width: none !important;
}

.small-title {
    max-width: none !important;
}

.bilibili-player-promote-wrap,
.ad-f,
.ad-e1,
.ad-e2,
.share-tool-bar,
#app_qrcode_box,
.video-top-ad,
.video-bottom-ad,
#playpage_mobileshow {
    display: none !important;
}

.video-desc, .desc-info {
    height: auto !important;
}

[report-id="abstract_spread"],
[report-id="abstract_unspread"] {
    display: none !important;
}

.video-tab>.video-list >*>*>.center-box>.content {
    display: inline !important;
}

.up-info .up-info_right {
    height: auto !important;
}
}
