/* ========== */
/* = Global = */
/* ========== */

html, body {
  /*background-color: #191b1c !important;*/
    background-color: #313537 !important;
}

.NB-static-alert {
  color: #ccc !important;
}

/* ============== */
/* = Scrollbars = */
/* ============== */

::-webkit-scrollbar {
    width: 8px !important;
}
::-webkit-scrollbar-track {
    background-color: rgba(245,250,255,.15) !important;
}
::-webkit-scrollbar-thumb {
    background-color: rgba(129, 129, 129, 0.5) !important;
}


/* =============== */
/* = Splash Pane = */
/* =============== */

/*
a.NB-splash-link:hover,
.NB-module-features .NB-module-feature .NB-module-feature-description a:hover {
  color: #a85b40 !important;
}
*/

.NB-module-premium .NB-module-item-intro {
  background-color: #aaa;
  border: 1px solid #ccc;
}

.NB-module-premium .NB-module-premium-price {
  color: #555;
}

/* Text color for "Premium Account", "New Features", "Community Feedback", "Past day", "Past day by hour" & "Recommended Site" body*/
.NB-module-content-header,
.NB-module-recommended .NB-recommended-description,
.NB-module-features .NB-module-feature .NB-module-feature-description {
  color: #ccc !important;
}

.NB-module-recommended .NB-recommended-added {
    color: #96C287;
}

/* Divider color */
.NB-module.NB-module-features .NB-module-content-header,
.NB-module-recommended .NB-recommended-description,
.NB-module-recommended .NB-recommended-title {
  border-bottom: 1px solid #777 !important;
}

/* "Recomended Site" title color */
.NB-recommended-title {
  color: #eee !important;
}

/* Link color */
a.NB-splash-link,
.NB-module-features .NB-module-feature .NB-module-feature-description a {
  color: #83b4e0 !important;
}

/* Background color of
	 "Premium Account", "Manage", "Past Day" & "Past Day by Hour*/
.NB-module-stats-counts {
  background-color: #444 !important;
}

/* "Logout", "Try" & "Add" button colors */
.NB-recommended-try, .NB-modal-submit-green .NB-modal-submit-button,
.NB-recommended-add, .NB-modal-submit-grey .NB-modal-submit-button,
.NB-menu-manage-logout {
	background-color: #505050 !important;
  background-image: none !important;
  color: #d5d4db !important;
  text-shadow: none;
}

/* Title colors of
	 "Premium Account", "Manage", "Past Day" & "Past Day by Hour* */
.NB-menu-manage-title,
.NB-module-stats-count-number {
  color: #ccc !important;
  text-shadow: 1px 1px 1px rgba(0,0,0,0.5) !important;
}

/* Graph color */
.NB-graph-value .NB-graph-bar {
  background-color: #83b4e0 !important;
}

/* Search input field color */
.NB-module-search-input input {
	color: #ccc;
  border: solid 1px #A8A8A8;
  background-color: #505050;
}

/* Bottom splash links color */
.NB-splash-info .NB-splash-links a{
	color: #83b4e0 !important;
	text-shadow: none !important;
}

/* ================ */
/* = Pop-up Menus = */
/* ================ */

/* Manage menu on splash page & cogwheel pop-up */
.NB-menu-manage-container,
.NB-menu-manage .NB-menu-manage-site-info, /* Preferences title background */
.NB-menu-item, /* Icon background */
.NB-menu-manage-title /* Title text background */ {
  background-color: #272d2e !important;
  box-shadow: none;
}

/* Title colors */
.NB-popover-section-title {
    color: #ccc;
}

/* Dividers */
.NB-popover-section {
    border-bottom: 1px solid #ccc;
}

.popover {
  box-shadow: none;
}

/* Pop-up menu arrows */
.popover .arrow,
.popover .arrow:after,
.popover.left .arrow,
.popover.left .arrow:after,
.popover.right .arrow ,
.popover.right .arrow:after,
.popover.top .arrow,
.popover.top .arrow:after,
.popover.top-left .arrow,
.popover.top-left .arrow:after,
.popover.top-right .arrow,
.popover.top-right .arrow:after,
.popover.bottom .arrow,
.popover.bottom .arrow:after,
.popover.bottom-left .arrow,
.popover.bottom-left .arrow:after,
.popover.bottom-right .arrow,
.popover.bottom-right .arrow:after {
  display: none !important
}

/* Add new site border color */
.NB-popover {
  background-color: #303739 !important;
  border-top: 1px solid #90928b !important;
  border-left: 1px solid #90928b !important;
  border-right: 1px solid #90928b !important;
  border-bottom: 1px solid #90928b !important;
  padding: 1px !important;
}

/* Add new site color */
.NB-add-form {
  color: #ccc;
  background-color: #303739 !important;
  border: none !important;
  padding: 0px !important;
}

/* Add new site input field and drop down menu */
.NB-input,
.NB-add input[type=text].ui-autocomplete-loading,
.NB-add-form .NB-folders{
  color: #ccc;
  background: none;
  background-color: #505050;
  border: solid 1px #90928b;
}

/* Unread/Oldest text color */
.NB-feedbar-options-stat .NB-stat {
	color: #ccc;
}

/* Style Font family active */
.segmented-control-vertical li.NB-active, .segmented-control-vertical li.NB-active:active, .segmented-control-vertical-active li {
  color: #ccc;
	background-color: #252D30;
  background-image: none;
}

/* Style Font family non-active */
.segmented-control-vertical li {
  color: #ccc;
  text-shadow: none;
	background-color: #303739;
  background-image: none;
}

