}

/* Hover color of date "3 days ago" */
.NB-interaction:hover:not(.NB-disabled) .NB-interaction-date {
    color: #5e828b;
}

/* Click color (no color at all) */
.NB-interaction:active:not(.NB-disabled) {
    border-top: 1px solid transparent;
    border-bottom: 1px solid transparent;
    background-color: transparent;
    background: none
}

/* New replies color */
.NB-interaction.NB-highlighted {
	background-color: #252D30;
  border-top: 1px solid transparent;
  border-bottom: 1px solid transparent;
}

/* Reply color */
.NB-interaction {
 	background-color: #303739;
  border-bottom: 1px solid transparent;
}

/* Username color */
.NB-splash-info .NB-splash-links a, .NB-static a, .NB-splash-link {
	color: #83B4E0;
}

/* "replied to your reply" text color */
.NB-interaction-title {
	color: #ccc;
}


/* ============================ */
/* = Task bars (top & bottom) = */
/* ============================ */

.NB-feeds-header-user,
.NB-feedbar,
.NB-taskbar-container,
#story_titles .NB-feedbar,
.NB-splash-info.NB-splash-bottom,
