css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://vk.com"), url-prefix("https://vk.com") {
	:root {
		--ts-input-width: 720px;   
		--player-left: 35px;
	}
	/* Левое меню */
		#side_bar_inner {
		position: fixed !important;
		top: 0;
		margin-top: 46px !important;
		z-index: 99;
	}
	/* Левое меню */

	.left_count_wrap, #side_bar ol li .left_row:hover .left_count_wrap, .font_medium .left_count_wrap, .font_medium #side_bar ol li .left_row:hover .left_count_wrap {
		text-align: center;
		background-color: #7C37B8;
		padding: 0 !important;
	}

	/* Счетчики слева */
	.left_count_wrap, #side_bar ol li .left_row:hover .left_count_wrap, .font_medium .left_count_wrap, .font_medium #side_bar ol li .left_row:hover .left_count_wrap {
    border-radius: 0;
    background: rgba(0, 0, 0, .09) !important;
    color: #777;
    height: 28px !important;
    width: 28px !important;
    margin: 0 0 0 0 !important;
    line-height: 28px;
  }
	/* Счетчики слева */

	/* Анимация лого */
	.top_home_link {
		position: relative;
		overflow: hidden;
	}
	.top_home_link:after {
		z-index: 0;
		position: absolute;
		width: 10px;
		height: 10px;
		top: calc(50% - 5px);
		left: calc(50% - 5px);
		content: '';
		visibility: hidden;
		border-radius: 50% !important;
		background: rgba(0, 0, 0, .5);

		opacity: 1;
	}

	.top_home_link .top_home_logo {
		margin: 13px auto 0;
	}

	/* Для userstyles */
    .top_home_link:hover:after {
    width: 10px;
    height: 10px;
    visibility: visible;
    animation-name: logocircle;
    animation-duration: .5s;
    animation-timing-function: ease-in-out;
    animation-fill-mode: both;
    transition-duration: .2s;
  }
    /* Для userstyles */

	.top_home_link:active:after {
		transform: scale(17) !important;
	}

	@-moz-keyframes logo {
		0% {
			transform: scale(1);
			opacity: 1;
		}
		100% {
			transform: scale(20);
			opacity: 0;
		}
	}
	@-webkit-keyframes logo {
		0% {
			transform: scale(1);
			opacity: 1;
		}
		100% {
			transform: scale(20);
			opacity: 0;
		}
	}

	@-moz-keyframes logocircle {
		0% {
			transform: scale(0);
			opacity: 1;
		}
		100% {
			width: 10px;
			height: 10px;
			transform: scale(10);
			opacity: .2;
		}
	}
	@-webkit-keyframes logocircle {
		0% {
			transform: scale(0);
			opacity: 1;
		}
		100% {
			width: 10px;
			height: 10px;
			border-radius: 50%;
			transform: scale(10);
			opacity: .2;
		}
	}
	/* Анимация лого */

	/* Анимация музыки в шапке */
	.top_audio_player .top_audio_player_play > div {
		margin: 0 auto 0;
	}

	.top_audio_player.top_audio_player_playing .top_audio_player_play > div {
		background-position: -15px -78px;
	}

	.top_audio_player .top_audio_player_play {
		width: 30px;
		height: 30px;
		padding: 0 !important;
		text-align: center;
		margin-top: 6px;
		border-radius: 50%;
	}

	.top_audio_player.top_audio_player_playing .top_audio_player_play {
    animation-name: playbtnaudio;
    animation-duration: 2.5s;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
    overflow: visible;
  }

	@-moz-keyframes playbtnaudio {
		0% {
			box-shadow: none;
		}
		50% {
			box-shadow: 0 0 0 15px rgba(0, 0, 0, .2);
			background-color: rgba(0, 0, 0, .2);
		}
		100% {
			box-shadow: 0 0 0 30px rgba(0, 0, 0, 0);
			background-color: rgba(0, 0, 0, 0);
		}
	}

	@-webkit-keyframes playbtnaudio {
		0% {
			box-shadow: none;
		}
		50% {
			box-shadow: 0 0 0 15px rgba(0, 0, 0, .2);
			background-color: rgba(0, 0, 0, .2);
		}
		100% {
			box-shadow: 0 0 0 30px rgba(0, 0, 0, 0);
			background-color: rgba(0, 0, 0, 0);
		}
	}
	/* Анимация музыки в шапке */

	.page_block {
		box-shadow: 0 1px 2px rgba(0, 0, 0, .14);
	}

	#page_header_cont .back {
		background: #7C37B8;
		box-shadow: 0 2px 4px rgba(0, 0, 0, .3);
		border: none;
	}

	#dev_top_nav_wrap {
		background: #7C37B8;                   
	}

	.dev_top_link, .dev_home_link {
		transition: all .24s ease-in-out;                      
	}

	.dev_top_link.sel, .dev_top_link.sel:hover, .dev_home_link:hover, .dev_top_link:hover {
		background: rgba(0, 0, 0, .1);                    
	}

	/* Плавающая кнопка */
	.chat_onl_wrap {
		bottom: 40px;
		right: 40px;
	}

	.chat_cont_sh_top, .chat_cont_sh_bottom {
		display: none !important; 
	}

	.chat_onl_inner {
		background-color: transparent !important; 
		overflow: visible;
	}

	#chat_tab_wrap {
		background: #7C37B8 url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 1)' height='24' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><path d='M0 0h24v24H0z' fill='none'/><path d='M16 11c1.66 0 2.99-1.34 2.99-3S17.66 5 16 5c-1.66 0-3 1.34-3 3s1.34 3 3 3zm-8 0c1.66 0 2.99-1.34 2.99-3S9.66 5 8 5C6.34 5 5 6.34 5 8s1.34 3 3 3zm0 2c-2.33 0-7 1.17-7 3.5V19h14v-2.5c0-2.33-4.67-3.5-7-3.5zm8 0c-.29 0-.62.02-.97.05 1.16.84 1.97 1.97 1.97 3.45V19h6v-2.5c0-2.33-4.67-3.5-7-3.5z'/></svg>") center center no-repeat;
		width: 56px;
		height: 56px;
		box-sizing: border-box;
		-moz-box-sizing: border-box;
		color: #fff;
		border-radius: 50%;
		box-shadow: 0 3px 6px rgba(0, 0, 0, .35);
		text-decoration: none;
		padding: 0;

		-moz-transition: all .24s ease-in-out;
		-webkit-transition: all .24s ease-in-out;
		transition: all .24s ease-in-out;                    
	}

	#chat_onl_wrap:hover #chat_tab_wrap {
		background: #7C37B8 url("data:image/svg+xml;utf8,<svg fill='rgba(255, 255, 255, 0)' height='14' viewBox='0 0 24 24' width='14' xmlns='http://www.w3.org/2000/svg'><path d='M0 0h24v24H0z' fill='none'/><path d='M16 11c1.66 0 2.99-1.34 2.99-3S17.66 5 16 5c-1.66 0-3 1.34-3 3s1.34 3 3 3zm-8 0c1.66 0 2.99-1.34 2.99-3S9.66 5 8 5C6.34 5 5 6.34 5 8s1.34 3 3 3zm0 2c-2.33 0-7 1.17-7 3.5V19h14v-2.5c0-2.33-4.67-3.5-7-3.5zm8 0c-.29 0-.62.02-.97.05 1.16.84 1.97 1.97 1.97 3.45V19h6v-2.5c0-2.33-4.67-3.5-7-3.5z'/></svg>") center center no-repeat;                    
	}

	#chat_tab_wrap:hover {
		box-shadow: 0 5px 14px rgba(0, 0, 0, .30);                      
	}

	.chat_fix_height {
		height: 0 !important;
		opacity: 0;
		margin-bottom: -48px;

		-moz-transition: all .44s ease-in-out;
		-webkit-transition: all .44s ease-in-out;
		transition: all .44s ease-in-out;
	}

	.chat_onl_height {
		transform: translateY(315px);   

		-moz-transition: all .24s ease-in-out;
		-webkit-transition: all .24s ease-in-out;
		transition: all .24s ease-in-out;
	}

	.chat_tab_wrap {
		margin: 0 auto;                      
	}

	#chat_onl_wrap:hover .chat_fix_height {
		height: 315px !important;   
		opacity: 1;
	}
	#chat_onl_wrap:hover .chat_onl_height {
		transform: translateY(0); 
	}

	.chat_onl_cont {
		padding: 0;    
	}

	.chat_onl {
		height: 56px;
		line-height: 56px;
		opacity: 0;
		transform: scale(1.45);
		font-size: 16px;
		color: #fff;

		-moz-transition: all .24s ease-in-out;
		-webkit-transition: all .24s ease-in-out;
		transition: all .24s ease-in-out;
	}
	.chat_onl:after {
		display: none;                      
	}

	#chat_onl_wrap:hover .chat_onl {
		opacity: 1;
		transform: scale(1);
	}

	.chat_cont_scrolling {
		overflow: hidden;                      
	}
	/* Плавающая кнопка */

	/* Долой рекламу! */ 
	#ads_left {
		display: none !important;
	}
	/* Долой рекламу! */

	/* Футер */
	#footer_wrap {
		padding: 30px 0;
		width: 100% !important;
	}
	#footer_wrap::after {
		content: 'Павел Дуров 4EVER';
		color: #777;
		font-size: 14px;
	}
	/* Футер */

	/* Лайк и репост */
	.post_full_like_wrap .post_like, .post_full_like_wrap .post_share {
		float: right;
	}
	.post_like, .post_share {
		margin: 0 !important;
	}

	.post_full_like_wrap .reply_link_wrap {
		float: left; 
	}
	/* Лайк и репост */

	/* Миничат */
	.fc_tab_wrap {
		width: 280px !important;
	}

	.rb_box_wrap {
		border-radius: 2px;
		box-shadow: 0 0 25px rgba(0, 0, 0, .1);
		right: auto;
		margin-right: 50px !important;
	}
	.rb_box_wrap .fc_tab_head {
		position: relative;
		background: #7C37B8;
		padding: 0;
		padding-right: 15px;
		height: 45px;
		box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
		z-index: 9;
		border: none !important;
	}
	.fc_tab_title {
		height: 100%;
		padding: 0;
		padding-left: 20px;
		line-height: 45px;
	}

	.fc_tab {
		background: #EBF0F5;
	}

	.fc_contact:first-child {
		padding-top: 8px;
	}
	.fc_contact {
		position: relative;
		padding: 8px 16px;
		height: auto;
		width: 100%;
		box-sizing: border-box;
		-moz-box-sizing: border-box;
	}

	span.fc_contact_photo, img.fc_contact_photo {
		border-radius: 50%;
	}

	.fc_contact:after {
		content: '';
		position: absolute;
		top: calc(50% - 5px);
		left: calc(50% - 5px);
		width: 0;
		height: 0;
		background: rgba(0, 0, 0, .05);
		visibility: visible;
		border-radius: 50%;

		-moz-transition: all .3s ease-in-out;
		-webkit-transition: all .3s ease-in-out;
		transition: all .3s ease-in-out;
	}
	.fc_contact:hover:after {
		width: 10px;
		height: 10px;
		transform: scale(30);
	}

	.fc_msgs_author {
		visibility: hidden;
	}

	div[id^="rb_box_fc_peer2000"] .fc_msgs_author {
		visibility: visible;
		top: 0;
		bottom: auto;
		left: 10px;
		border-radius: 50%;
		vertical-align: top;
	}
	img.fc_msgs_author {
		width: 30px;
		height: 30px;
	}

	.rb_resize_wrap {
		display: none;
	}

	.fc_msgs_wrap {
		padding-top: 0;
		padding-left: 0;
		margin-top: 10px;
		padding-right: 31px;
		-webkit-filter: drop-shadow(0px 1px 1px rgba(0,0,0,.1));
		filter: drop-shadow(0px 1px 1px rgba(0,0,0,.1));

		transition-duration: .3s;
	}

	.fc_tab_close_wrap, .fc_tab_min_wrap, .fc_tab_max_wrap, .fc_tab_pin_wrap {
		margin-top: 16px;
	}
	.fc_msgs {
		margin-left: 22px;
		padding: 14px 18px;
		border-radius: 0 5px 5px 5px;
	}
	.fc_msg.fc_msg_unread {
		background: transparent !important;
		color: #000 !important;
	}

	div[id^="rb_box_fc_peer2000"] .fc_msgs {
		margin-left: 76px;
	}
	div[id^="rb_box_fc_peer2000"] .fc_msgs_author {
		padding: 0 12px 0 8px;
	}

	.fc_msgs_out .fc_msgs {
		margin-left: 22px;
		border-radius: 5px 0 5px 5px;
	}
	.fc_in_msg_arr {
		left: 8px;
		top: 0;
	}
	div[id^="rb_box_fc_peer2000"] .fc_in_msg_arr {
		left: 42px;
	}

	.fc_tab_pin_wrap, .fc_tab_max_wrap, .add_media_pointer, .chats_sp.fc_tab_pointer {
		display: none;
	}
	.fc_tab_max_wrap {
		display: block;
	}
	.fc_tab_txt_wrap, div.fc_clist_filter_wrap {
		height: auto !important; 
		background: #fff;
	}
	div.fc_clist_filter_wrap {
		padding: 0;
		border: none;
	}

	#fc_contacts {
		box-shadow: inset 0 0 5px rgba(0, 0, 0, .1);
	}

	.chats_sp.fc_clist_search_icon {
		margin-top: 16px;
		margin-left: 15px;
	}

	.fc_clist_filter_wrap {
		box-shadow: 0 -1px 5px rgba(0, 0, 0, .1);
		border: 1px solid red;
	}

	div.fc_tab_txt, div.fc_clist_filter {
		border: none;
		height: 100%;
	}
	div.fc_clist_filter {
		padding-top: 8px;
		min-height: 35px !important;
	}

	.fc_clist_online {
		margin-top: 15px;
		margin-left: -20px;
	}

	.fc_tab_attach {
		padding-left: 15px;
		padding-top: 10px;
		height: auto;
	}

	.fc_msgs {
		position: relative;
		overflow: visible;
	}

	.fc_msgs::before {
		content: '';
		position: absolute;
		left: -15px;
		top: 0 !important;
		width: 15px;
		height: 24px;
		background-position: 0 0;
	}
	.fc_msgs_wrap:not(.fc_msgs_out) .fc_msgs::before {
		background: url(http://ipic.su/img/img7/fs/left-corner2.1454123851.png) no-repeat;
		background-size: contain;
	}
	.fc_msgs_wrap.fc_msgs_out .fc_msgs::before {
		left: auto;
		right: -15px;
		background: url(http://ipic.su/img/img7/fs/right-corner2.1454124088.png) no-repeat;
		background-size: contain;
	} 

	.fc_msgs_unread {
		background: transparent;
		background: url(http://ipic.su/img/img7/fs/icon.1454162442.png) no-repeat;
		background-position: 25px calc(100% - 15px);
		background-size: 8px;
	}
	.fc_msgs_wrap:not(.fc_msgs_out).fc_msgs_unread {
		background-position: calc(100% - 25px) calc(100% - 15px);
	}

	.fc_msg {
		padding: 0;
	}

	.fc_msgs_out .fc_msgs {
		background: #D7E7FA;
		border: none;
	}

	.fc_msgs {
		border: none;
	}

	.fc_tab_attach .ms_items_more_wrap {
		padding: 0;
	}
	/* Миничат */

	/* Выбор диалогов */
	.im-page .im-page--history {
    margin-left: 0;
    margin-right: 317px;
    z-index: 104;
  }
  .im-page .im-page--dialogs {
    float: right;
  }
  .im-create {
    right: 0;
  }
  .nim-dialog_unread-out, .im-mess_unread, .nim-dialog_unread {
    background: #f5f5f5;
  }
  .im-mess {
    border-radius: 3px;
  }
	/* Выбор диалогов */

	body.dev input.dev_top_input {
		transition: all .24s ease-in-out;                      
	}

	input.text.ts_input, body.dev input.dev_top_input {
		background-color: rgba(0, 0, 0, .1);
	}

	.button_blue button, .button_gray button, .button_light_gray button, .flat_button {
		-moz-transition: all .24s ease-in-out;
		-webkit-transition: all .24s ease-in-out;
		transition: all .24s ease-in-out;
		background-color: transparent;
		color: #777;
		text-transform: uppercase;
	}
	.button_blue button.hover, .button_blue button:hover, .flat_button.hover, .flat_button:hover {
		background-color: #eee;
	}
	.page_photo .flat_button, .page_photo .page_actions_expanded, .page_photo .page_actions_wrap {
		text-transform: initial;                    
	}
	.page_actions_cont.narrow .page_actions_header, .page_actions_cont.narrow .page_extra_actions_btn {
		transition-duration: 0s;                      
	}

	.wide_column .topics_module .topic_title,
	.topics_module .topic_inner_link,
	a,
	.post_author .author, .wall_module .author, .wall_module .copy_author,
	.post_like, .post_share,
	.wall_module .my_like .post_like_count, .wall_module .my_share .post_share_count,
	.page_doc_row .page_doc_title,
	.page_counter .count,
	.page_counter:hover .label {
		color: #7C37B8;
	}

	.ui_tab_sel, .ui_tab_sel:hover, .ui_tabs .ui_tab_sel, .ui_tabs .ui_tab_sel:hover, .ui_tabs_box .ui_tab_sel, .ui_tabs_box .ui_tab_sel:hover {
		border-bottom: 2px solid #7C37B8;
	}

	/* Анимация профиля в шапке */
	#top_profile_link, .top_nav_link, .top_nav_btn, #top_notify_btn .top_nav_btn_icon {
		position: relative;
		overflow: hidden;
	}

	.top_nav_link, .top_nav_btn {
		-moz-transition: all .24s ease-in-out;
		-webkit-transition: all .24s ease-in-out;
		transition: all .24s ease-in-out;
	}
	.top_nav_link:hover, .top_nav_btn:hover, .top_nav_link.active, .top_nav_btn.active {
		background-color: rgba(0, 0, 0, 0) !important;
	}

	#top_profile_link:after, .top_nav_link:after, .top_nav_btn:after, #top_notify_btn .top_nav_btn_icon:after {
		position: absolute;
		content: '';
		width: 0;
		height: 0;
		left: calc(50% - 5px);
		top: calc(50% - 5px);
		background: rgba(0, 0, 0, .1);
		border-radius: 50%;

		-moz-transition: all .24s ease-in-out;
		-webkit-transition: all .24s ease-in-out;
		transition: all .24s ease-in-out;
	}
	#top_profile_link:hover:after, .top_nav_link:hover:after, .top_nav_btn:hover:after, .top_nav_btn.active:after, .top_nav_link.active:after, #top_notify_btn:hover .top_nav_btn_icon:after, #top_notify_btn.active .top_nav_btn_icon:after {
		width: 10px !important;
		height: 10px !important;
		transform: scale(17);
	}

	#top_notify_btn {
		overflow: visible;
		float: left;
	}
	#top_notify_btn:after, #top_notify_btn .top_nav_btn_icon:after {
		display: none;
	}
	/* Анимация профиля в шапке */

	/* Новые посты */
	#feed_new_posts {
		position: fixed;
		width: 240px;
		top: 50px;
		box-shadow: 0 3px 10px rgba(0, 0, 0, .3);
		height: 35px;
		line-height: 35px;
		color: #fff;
		background: #7C37B8;
		padding: 0 15px;
		border-radius: 20px;
		left: calc(50% - 120px);
		z-index: 99;
		border: none;

		-moz-box-sizing: border-box;
		box-sizing: border-box;

		-moz-transition: all .3s ease-in-out;
		-webkit-transition: all .3s ease-in-out;
		transition: all .3s ease-in-out;
	}
	#feed_new_posts:hover {
		background: #7C37B8;
		box-shadow: 0 3px 10px rgba(0, 0, 0, .5);
	}
	/* Новые посты */

	/* Нумерация страниц */
	#pg_fixed {
		top: 60px;
	}

	.pg_fixed_pages {
		display: flex;
		flex-direction: row;
		align-items: center;
		justify-content: center;
		border-radius: 20px;
		padding: 5px;
	}
	.pg_fixed_back {
		border-radius: 20px;
		opacity: .6;
	}
	.pg_flnk, .pg_flnk_rd, .pg_flnk_sel, .pg_flnk_nb {
		display: block !important;
		min-width: 20px;
		height: 20px;
		line-height: 20px;
		text-align: center;
		padding: 0 2px;
		border: none;
		border-radius: 10px;
	}
	.pg_flnk_sel {
		border: none;
		background: #fff;
		color: #444;
	}

	.im-chat-input {
		position: relative;
	}

	#translate-button {
		border: none;
		width: 40px;
		height: 40px;
		position: absolute;
		top: -20px;
		left: 68px;
		border-radius: 50%;
		visibility: hidden;
