css:
@-moz-document url("http://animeandmangaclub.net/LiveChat") {
#webpage-contentborderimage {
	background: #FFFFEE url(http://s.4cdn.org/image/fade.png) top center repeat-x;
}

.crimson-background, .orange-background, .accent-background {
	background-color: transparent;
    color: #cc1105;
    font-weight: bold;
    font-size: 10pt;
    font-family: arial,helvetica,sans-serif;
}

.primary-background {
	background: transparent;
}

.card-shadow {
	box-shadow: none;
}

.MaterialShadow, .material-shadow {
	box-shadow: none;
}

.block-center {
	margin-top: 160px;
    border: none;
    border-top: 1px solid #D9BFB7;
    height: 0;
}

.unfocused-background {
	background-color: transparent;
}

}

@-moz-document url("http://animeandmangaclub.net/LiveChat") {
/* AMC nav */

#navBar-topBar /* banner */ {
	background-image: url(http://i.imgur.com/6nD1hDS.png);
    background-repeat: no-repeat;
    background-position: center;
    background-color: transparent;
    background-size: auto;
    height: 100px;
    border: 1px solid #800;
    margin-left: 450px;
    width: 300px;
    max-width: 100%;
    margin-top: -35px;
}

#navBar-anchors, #navBar-smallScreenNavTrigger {
	font-size: 9pt;
    background-color: transparent;
    margin-left: -350px;
    margin-top: -125px;
    font-weight: normal;
    display: block;
    
}


#above-banner-bar {
	border: none;
    box-shadow: none;
}

#navBar-anchors > a {
    padding: 1px;
	color: #800000;
    text-transform: lowercase;
    font-weight: normal;
}

#navBar-anchors > a:hover {
   color: red!important;
   background-color: transparent;
}


#navBar-anchors > a::after {
	content: " / ";
}

#navBar-anchors a[href="/Community"]::before {
	content: "[";
}

#navBar-anchors a[href="/LiveChat"]::after {
	content: "]";
}

.block-center {
	margin-top: 160px;
    
}

.button[href="/"] {
	padding: 1px;
    color: #800000;
    text-transform: lowercase;
    font-weight: normal;
    font-size: 9pt;
    margin-left: -194px;
    margin-bottom: 39px;
    display: block;
}

.button[href="/"]::before {
	content: "[";
}

.button[href="/"]::after {
	content: "]";
}

.button[href="/"]:hover {
	color: red !important;
    background-color: transparent;
}

small {
	margin-left: 10px;
    margin-top: -120px;
    display: block;
    border: none;
    border-top: 1px solid #D9BFB7;
    height: 0;
    padding: 30px;
    font-size: 9pt;
    color: #800000;
    font-weight: normal;
    text-align: justify;
}

#navBar-userZone-avatar, #usermenu-avatar {
	display: none;
}

#navBar-userZone-userDrawerActivator {
	font-size: 9pt;
    margin-top: -36px;
    margin-right: -50px;
    text-transform: lowercase;
    font-weight: normal;
}

.text-color-1 {
	color: #800000;
}
}

@-moz-document url("http://animeandmangaclub.net/LiveChat") {
/* AMC chat */

.liveChatPostContainer {
	margin-left: -700px;
    margin-top: 0px;
    max-width: 800px;
       
}

#usersInChatContainer {
margin-left: 1200px;
    width: 300px;
}

#live-chat-input {
    margin-left: -700px;
    margin-top: -300px;
    border: 1px solid #aaa;
    font-family: arial,helvetica,sans-serif;
    font-size: 10pt;
    outline: medium none;
    width: 296px;
    padding: 10px;
    
}

#live-chat-input:checked {
	background-color: lightgrey;
    width: 300px;
    border: 25px solid green;
    padding: 25px;
    margin: 25px;
}

.liveChatPostAvatar {
	
}

.liveChatPostBody {
    padding: 50px;
    background-image: none;
    background-color: #f0e0d6;
    border: 1px solid #D9BFB7;
    border-left: none;
    border-top: none;
    color: #800000;
    font-size: 10pt;
    text-shadow: none;
    font-weight: normal;
    letter-spacing: normal;
}

.liveChatPostUsername {
	
}

::-webkit-scrollbar-thumb {
	display: none;
}

::-webkit-scrollbar-track {
	display: none;
}

.green-background {
	background: none;
    color: white;
}

.gradient-red-background {
	background: none;
    color: white;
}

div.liveChat-adminBadge.hidden.crimson-background.light-padding.small-font.text-center.card-shadow {
	background: none;
    color: white;
}
}
