/* Menu A PROPOS DU STREETART*/
p.ng-scope:nth-of-type(1){
	font-size: 0;
}

p.ng-scope:nth-of-type(1):before {
	content: "Cette collection en cours, faite par vous, présente le StreetArt du monde entier. Version du navigateur: www.spotteron.com/streetart";
