css:
@-moz-document domain("hltv.org") {

/**** Make HLTV Fluid Again */
.bgPadding, 
.widthControl,
.navbar .navcon {
    max-width: none;
}

h2.newsheader ~ .standard-box:nth-child(3) .newstext {
    font-size: 19px;
}

.rightCol {
    max-width: 220px;
}

.right2Col {
    max-width: 180px;
}

.navbar .small-logo,.navbar.showSmall .small-logo {
    width: 0;
}

/**** Improvements */
/* Better space usage */
.contentCol {
    background: #fff;
    border: 1px solid #cfd1d4;
    padding: 1em;
}

.bgPadding {
    padding-top: 0;
}

.logoCon {
    border-bottom: 1px solid #e1e1e1;
    background: white;
    padding: 20px;
    margin: 0 -10px 0;
    position: relative;
}

.colCon > div {
    padding-top: 20px;
}

/* Hot matches stars highlighting */
.teambox.match.star1:after,
.teambox.match.star2:after,
.teambox.match.star3:after,
.teambox.match.star4:after,
.teambox.match.star5:after {
    display: block;
    background: #fffa78;
}

.teambox.match {
    position: relative;
}

.teambox.match *,
.teambox.match:before {
    z-index: 1;
}

.teambox.match:after {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    content: '';
    display:none;
    z-index: 0;
}

.teambox.match.star1:after {opacity: 0.1; }
.teambox.match.star2:after {opacity: 0.2; }
.teambox.match.star3:after {opacity: 0.35; }
.teambox.match.star4:after {opacity: 0.6; }
.teambox.match.star5:after {opacity: 0.9;}

/* News today visual hierarchy */
.standard-box.standard-list {
    font-size: 1.25em;
    background: #f9f9f9;
}

.standard-box.standard-list ~ .standard-box.standard-list,
.standard-box.standard-list ~ .old-news-con .standard-box.standard-list {
    font-size: 1em;
    background: #fff;
}

.index .newstext {
    font-size: 1em;
    padding: 0.1em 0;
}

}
