css:
@-moz-document url-prefix("http://www.blipfoto.com"), url-prefix("https://www.blipfoto.com")
{
  .browse_grid li .image .text .title
  {
    opacity: 0;
    transition: all 0.2s;
  }

  #main
  {
    background: #444 !important;
  }

  .browse_grid li .image img:hover
  {
  }

  .browse_grid li .image:hover  .text .title
  {
    opacity: 0.5;
  }

  .browse_grid.large-block-grid-6 li
  {
    padding-bottom: 16.6667%;
  }

  .browse_grid li .image
  {
    height: 170px;
    width: 170px;
  }

  body
  {
    color: #aaa;
  }

  div.entryText,
    #biography-full, 
    .comments #commentlist ul li p
  {
    background: none repeat scroll 0 0 #444;
    border-radius: 5px;
    color: #aa8;
  }

  div.entryText h1
  {
    color: #aaa;
  }

  #comments textarea, .sceditor-container
  {
    background-color: #555 !important;
    border: 1px solid #666 !important;
    border-radius: none !important;
  }

  .sceditor-html, .sceditor-html body
  {
    color: #aa8 !important;
  }

  .entryImage
  {
    background: #444 !important;
    border-color: #444 !important;
  }

  #commentlist a,
    .entryText a,
    .profile-sidebar a,
    .pagination a:hover,
    .pagination li:last-child:hover a:before,
        .pagination li:last-child:hover a:after
  {
    color: #ffcc00 !important;
  }

  .nav.segmented a
  {
    color: #f2f2f2 !important;
  }

  .nav.segmented a:hover
  {
    color: #aa8 !important;
  }

  .nav.segmented .active a
  {
    color: #444 !important;
  }

  div.usercallout  .text
  {
    color: #aa8 !important;
  }

  div.usercallout  .text h2
  {
    color: #aaa !important;
  }

  div.usercallout  .text a i, 
    div.usercallout  .text a span
  {
    color: #ffcc00 !important;
  }

  #entryHeader .entryNavigation > ul > li a
  {
    background-color: #222 !important;
    color: #aaa !important;
  }

  #entryHeader .entryNavigation > ul > li a:hover
  {
    background-color: #333 !important;
    color: #bbb !important;
    transition: all 0.2s;
  }

  .actions li:first-child a, .actions li:first-child span, .actions .action:first-child a, .actions .action:first-child span,
    .actions li a, .actions li span, .actions .action a, .actions .action span
  {
    border: none !important;
    color: #888 !important;
    border-radius: 0 !important;
  }

  .actions li a:hover, .actions .action a:hover
  {
    background: #444 !important;
    color: #aaa !important;
  }

  .entryStatsContainer .views
  {
    border: none !important;
    color: #888 !important;
  }

  .frame.stacked a:before, .frame a:after
  {
    background: none repeat scroll 0 0 #333;
    border: 1px solid #111;
  }

  .frame .image_wrapper
  {
    border: 1px solid #777;
  }

  .nav.segmented li.active
  {
    background: none repeat scroll 0 0 #999;
  }

  .sceditor-container
  {
    background-color: #fff !important;
  }
}
