css:
@-moz-document url-prefix("https://www.wanikani.com/kanji"), url-prefix("https://www.wanikani.com/radicals"), url-prefix("https://www.wanikani.com/level"), url-prefix("https://www.wanikani.com/vocabulary") {
.multi-character-grid.multi-character-grid li[id^="vocabulary"] a {
	display: flex;
	justify-content: space-between;
	margin-right: 0;
}

.multi-character-grid.multi-character-grid li[id^="vocabulary"] span.character {
	font-size: 2.8rem !important;
	align-self: center !important;
	font-weight: normal !important;
}

.multi-character-grid.multi-character-grid li[id^="vocabulary"] ul {
	display: flex;
	float: none;
	flex-direction: column;
	align-content: center;
	font-size: 1.6rem;
	padding: 0;
}

.multi-character-grid.multi-character-grid li[id^="vocabulary"] ul li:last-of-type{
	font-size: 1.1rem;
}

.single-character-grid {
	display: flex !important;
	flex-flow: row wrap !important;
}

.single-character-grid li {
	padding: .2rem .5rem .7rem !important;	
	flex: 1 0 15% !important;
	max-width: 180px !important;
}

.single-character-grid li a {
	display: flex !important;
	flex-direction: column !important;
}

.single-character-grid li span.character {
	font-size: 5rem !important;
	font-weight: normal !important;
	text-align: center !important;
	line-height: 1.4 !important;
}

.single-character-grid li ul {
	display: flex !important;
	float: none !important;
	flex-direction: column !important;
	align-content: center !important;
	font-size: 1.8rem !important;
	padding: 0 !important;
	text-align: center !important;
}


.single-character-grid li ul li {
	line-height: 1 !important;
}

.single-character-grid li ul li:last-of-type{
	font-size: 1rem !important;
}

.single-character-grid li[id^="radical"] ul li:first-of-type{
	display: none;
}

.level-icon, 
.kanji-icon, 
.radical-icon, 
.vocabulary-icon {
	font-size: 4rem !important;	
  width: 1.5em !important;
	height: 1.5em !important;
	font-weight: normal !important;
	line-height: 1.5 !important;
}

.vocabulary-icon {
	width: auto !important;
}

.enlarge-hover {
	font-size: 10rem !important;
}

.pager a {
	font-size: 1.6rem !important;	
	color: #BBB !important;
}
.pager a:hover {
	color: #AAA !important;
}
}

@-moz-document url-prefix("https://www.wanikani.com/lesson"), url-prefix("https://www.wanikani.com/review") {
/* Horizontal vocabulary bars */
div[id*="summary"] li.radicals a, 
div[id*="summary"] li.kanji a,
div[id*="summary"] li.vocabulary a {
	font-size: 1.9rem !important;
	padding: .5rem .8rem !important;
}

div[id*="summary"] li.radicals .hover, 
div[id*="summary"] li.kanji .hover,
div[id*="summary"] li.vocabulary .hover {
	font-size: 1.3rem !important;
	padding: .4rem .5rem !important;
}
}

@-moz-document url("https://www.wanikani.com"), url("https://www.wanikani.com/dashboard") {
.kotoba-table-list.dashboard-sub-section table {
	display: flex;
	flex-flow: column;
	line-height: normal;
	width: 100%;
}

.kotoba-table-list.dashboard-sub-section td, 
.kotoba-table-list.dashboard-sub-section tr {
	display: block;
	width: 100%;
}

.kotoba-table-list.dashboard-sub-section tr a {
	display: flex;
	justify-content: space-between;
	align-items: center;
	padding: .2rem;
}

.kotoba-table-list.dashboard-sub-section tr a span {
	font-size: 1.5em;
	padding: .2em;
}

.kotoba-table-list.dashboard-sub-section tr a span[lang="ja"] { 
	font-size: 2.4em;
}
}
