css:
@-moz-document domain("lichess.org")
{
body.dark
  {
    background-image: url("https://i.imgur.com/ySAw3l2.png")  !important;
    color: #EEE;
    
  }
  .purple .is2d .cg-board,
#top div.color_demo.purple
  {
    background-image: url("https://i.imgur.com/JT2aErj.png")  !important;
  }
.is2d .cg-board
  {
    image-rendering: auto;
  }

  .lichess_board
  {
    padding: 3px;
    background-image: url(https://i.imgur.com/WAnQnxF.png)  !important;
  }

  .underboard
  {
    margin-top: 50px !important;
  }

  .ranks coord
  {
    margin-left: 2px !important;
  }

  .files coord
  {
    margin-top: 0px !important;
  }
.cg-board square.selected
  {
    background-color: rgba(216,80,0,0.5) !important;
  }

  .cg-board square.move-dest
  {
    background: radial-gradient(rgba(216,80,0,0.3) 17%,#208530 0,rgba(0,0,0,0.3) 0,rgba(0,0,0,0) 0) !important;
  }
.cg-board square.last-move 
  {
    background-color: rgba(216,80,0,0.5) !important;
   }
}
