.top_home_link {
    position: relative;
    overflow: hidden;
}
.top_home_link:after {
    z-index: 0;
    position: absolute;
    width: 10px;
    height: 10px;
    top: calc(50% - 5px);
    left: calc(50% - 5px);
    content: '';
