/* √ê¬î√ê¬ª√ë¬è userstyles */

.top_home_link:hover:after {
    width: 10px;
    height: 10px;
    visibility: visible;
    animation-name: logo;
    animation-duration: .5s;
    animation-timing-function: ease-in-out;
    animation-fill-mode: both;
    transition-duration: .2s;
}
/* √ê¬î√ê¬ª√ë¬è userstyles */

.top_home_link:active:after {
    transform: scale(17) !important;
}
@-moz-keyframes logo {
    0% {
        transform: scale(1);
        opacity: 1;
    }
    100% {
        transform: scale(20);
        opacity: 0;
    }
}
@-webkit-keyframes logo {
    0% {
        transform: scale(1);
        opacity: 1;
    }
    100% {
        transform: scale(20);
        opacity: 0;
    }
}
@-moz-keyframes logocircle {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        width: 10px;
        height: 10px;
        transform: scale(10);
        opacity: .2;
    }
}
@-webkit-keyframes logocircle {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        width: 10px;
        height: 10px;
        border-radius: 50%;
        transform: scale(10);
        opacity: .2;
    }
