#top_profile_link,
.top_nav_link,
.top_nav_btn,
#top_notify_btn .top_nav_btn_icon {
    position: relative;
    overflow: hidden;
}
.top_nav_link,
.top_nav_btn {
    -moz-transition: all .24s ease-in-out;
    -webkit-transition: all .24s ease-in-out;
    transition: all .24s ease-in-out;
}
.top_nav_link:hover,
.top_nav_btn:hover,
.top_nav_link.active,
.top_nav_btn.active {
    background-color: rgba(0, 0, 0, 0) !important;
}
#top_profile_link:after,
.top_nav_link:after,
.top_nav_btn:after,
#top_notify_btn .top_nav_btn_icon:after {
    position: absolute;
    content: '';
    width: 0;
    height: 0;
    left: calc(50% - 5px);
    top: calc(50% - 5px);
    background: rgba(0, 0, 0, .1);
    border-radius: 50%;
    -moz-transition: all .24s ease-in-out;
    -webkit-transition: all .24s ease-in-out;
    transition: all .24s ease-in-out;
}
#top_profile_link:hover:after,
.top_nav_link:hover:after,
.top_nav_btn:hover:after,
.top_nav_btn.active:after,
.top_nav_link.active:after,
#top_notify_btn:hover .top_nav_btn_icon:after,
#top_notify_btn.active .top_nav_btn_icon:after {
    width: 10px !important;
    height: 10px !important;
    transform: scale(17);
}
#top_notify_btn {
    overflow: visible;
    float: left;
