@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org")
{
	/* Text and link defaults */

	*
	{
		color: #bbb !important;
	}
	
	a[href], a[href]:visited, a[href]:hover, #title_long a, #title_long a:hover,
	a[href] *, a[href]:visited *, a[href]:hover *
	{
		color: #19f !important;
	}
	a[href]:hover,
	#title_long a:hover,
	a[href]:hover *
	{
		color: #f91 !important;
	}
	
	/* ---- MAIN WEBSITE ---- */
	
	/* Main elements */
	
	#root
	{
		background-color: #111;
	}
	
	#header
	{
		background-color: #222;
	}
	
	#nav-view
	{
		background-color: #111;
		margin-bottom: 15px;
	}
	
	#view
	{
		background-color: #222;
		margin-right: 0;
		padding-right: 20px;
		padding-left: 20px;
		padding-top: 20px;
	}
	/* For iframes used for user profiles */
	body
	{
		background-color: #222;
	}
	
	#navigation
	{
		background-color: #222;
		margin-right: 15px;
		margin-bottom: auto;
		padding-top: 20px;
		padding: 20px 20px 20px 30px;
	}
	
	#footer
	{
		background-color: #222;
	}
	
	#view_inner,
	.styles
	{
		width: 730px;
	}
	
	/* Style lists */
	.style_card
	{
		background-color: #333;
		border: 5px solid #333;
		box-shadow: none !important;
		border-radius: 0;
	}
	.screenshot_wrapper
	{
		border-width: 0;
		display: block;
	}
	.screenshot_img,
	.screenshot_img_long
	{
		border-radius: 0;
	}
	
	.line_seperator
	{
		display: none;
	}
	.style_card_long
	{
		background-color: #292929;
		box-shadow: none !important;
		margin-bottom: 10px;
		padding-left: 10px;
	}
	
	/* Sidebar */
	
	#navigation a .navigation_link
	{
		box-shadow: none;
	}
	#navigation a .navigation_link 
	{
		background-color: #252525;
	}
	#navigation a:nth-child(even) .navigation_link 
	{
		background-color: #282828;
	}
	#navigation a:hover .navigation_link 
	{
		background-color: #111;
	}
	
	/* Style's page */
	
	#information_div
	{
		margin-top: 10px;
	}
	#left_information,
	#center_information,
	#right_information
	{
		background-color: #111;
		padding: 10px 20px;
		margin-bottom: auto;
	}
	#left_information > *:first-child
	{
		margin-top: 0;
	}
	
	.text_style_page,
	#notes_textarea
	{
		white-space: pre-wrap;
		background-color: #111;
		padding: 10px 20px;
	}
	
	#stylish-code
	{
		font-family: monospace;
		white-space: pre-wrap;
		background-color: #111;
		padding: 2px 5px;
		border: none;
		border-radius: 0;
	}
	
	#applies_link
	{
		background-color: #111;
		padding: 10px 20px;
	}
	
	.advancedsettings_shown
	{
		background-color: #111;
	}
	.style-setting-option
	{
		background-color: #222;
		border-color: #222;
	}
	#update_style_button *
	{
		color: white !important;
	}
	
	/* Buttons */
	.see_more,
	.css_button,
	.advanced_button,
	.view_details,
	.white_button,
	#send_feedback_button,
	#style_installed_button,
	#archive_style_button
	{
		background-color: #333;
		box-shadow: unset !important;
	}
	.see_more:hover,
	.css_button:hover,
	.advanced_button:hover,
	.view_details:hover,
	.white_button:hover,
	#send_feedback_button:hover,
	#style_installed_button:hover,
	#archive_style_button:hover
	{
		background-color: #111;
	}
	#send_feedback_button
	{
		border-color: #555;
	}
	.white_button
	{
		border-color: #555;
	}
	
	/* Search */
	#search_input
	{
		background-color: #111 !important;
	}
	
	/* Style list page numbers */
	.pageNumber.current,
	.div-arrow
	{
		background-color: #111;
	}
	.pageNumber:hover,
	.div-arrow:hover
	{
		background-color: #333;
		border-radius: 4px;
	}
	.pageNumber span
	{
		color: #bbb !important;
		font-weight: bold;
	}
	.div-arrow
	{
		background-color: #111;
		border-width: 0;
	}
	
	/* Loged in user page */
  .obsolete a
	{
		color: #07a !important;
	}
	table *
	{
		border-color: #222;
		background-color: #191919;
		padding: 2px 4px;
	}
	
	/* New style */
	input,
	textarea
	{
		background-color: #282828;
		border-width: 0;
	}
	input.color
	{
		background-color: #282828 !important;
	}
	#edit-style-settings input
	{
		margin: 4px;
	}
	.edit-setting
	{
		background-color: transparent;
	}
	
	.errorExplanation
	{
		background-color: #733;
	}
	
	/* Code */
	pre,
	code
	{
		background-color: #fff1;
		border-width: 0;
	}
	pre code
	{
		background-color: transparent;
	}
}

@-moz-document domain("forum.userstyles.org")
{
	/* ---- FORUM WEBSITE ---- */
	
	body,
	#Frame
	{
		background-color: #111;
	}
	#Head
	{
		background-color: #222;
	}
	#Panel
	{
		background-color: #222;
		padding: 10px;
	}
	
	/* Does not work? */
	.Button[class]
	{
		background-color: #333 !important;
		box-shadow: none !important;
	}
	.Button:hover
	{
		background-color: #111 !important;
	}
	
	/* Sidebar */
	#Panel li
	{
		border-width: 0 !important;
	}
	#Panel li
	{
		background-color: #252525;
	}
	#Panel li:nth-child(even) 
	{
		background-color: #282828;
	}
	#Panel li:hover
	{
		background-color: #111;
	}
	
	/* Discussion list */
	.DataList .Item
	{
		background-color: #151515;
	}
	.DataList .Item:nth-child(even) 
	{
		background-color: #1a1a1a;
	}
	.DataList .Item:hover
	{
		background-color: #222;
	}
	.Title a[href]
	{
		color: #bbb !important;
	}
	.Title a[href]:hover
	{
		color: #f91 !important;
	}
	.MItem
	{
		color: #555 !important;
	}
	.Tag,
	.NewCommentCount
	{
		background-color: #222;
	}
	.MenuItems
	{
		background-color: #222;
		border-color: #555;
		color: #bbb !important;
	}
	.MenuItems li a:hover
	{
		background-color: #333 !important;
	}
	.MenuItems li a[href]
	{
		color: #bbb !important;
	}
	
	input.DateBox,
	input.InputBox,
	input.SmallInput,
	textarea
	{
		background-color: #333 !important;
	}
	
	/* Thread */
	blockquote.Quote,
	blockQuote.UserQuote
	{
		background-color: #fff1;
	}
	.EmoticonContainer
	{
		background-color: #222 !important;;
	}
}
