css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com"),
domain("consumerist.com")
 {
#header_container,
#skyscraper,
#commentsinterruptor-holder,
#interruptor,
#ask_ads_part_1,
#adsense-interruptor
{display:none !important;}

#skySpacer {height:0 !important; border-top:0 !important; margin-top:0 !important;}

#menubar {margin-top:32px !important;}

#interruptor {border-top:1px solid #B3B3B3 !important; width:100% !important;}

/* Move search to right */
#search {left:auto !important; right:10px !important;}
/* Move menus together between search and header */
.yui-skin-sam ul.middle {margin-left:0 !important;}
#navigation {float:left !important;}

div.post a.featured {float:right; margin-top:-3.5em !important;}

#text {height:300px !important;}
.comments_new_box .content {height:auto !important; padding-bottom:1em !important;}

.metadata, .metadata * {color:#000 !important;}
li.post .metadata, li.quicklink .metadata {width:230px !important;}
li.post .excerpt, li.quicklink .excerpt {width:575px !important;}
.post .excerpt img.bigpic {display:block;}
.post .excerpt .galleryPreview {overflow:hidden;}

#content li.post, #content li.quicklink {margin-bottom:30px !important;}

#Posts {margin-top:3em !important;}

}
