css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("haruka-yumenoato.net") {
  header #header, #front .article-box section .links a.label-silver, #front .article-box section .links a, footer #mainfooter, header #menubar
  { background-color: #121212 !important; }
  header #header .timeline, #front .front-box .search-box input, #front .article-box section h2, #container .contents .head, footer #mainfooter .right .calendar .title, .common-button, #front .latest-request .elem .category span, #front .ranking .elem .context .detail .category, #front .article-box section .links a b, header #bar .search input, #article article .wrapper .left .detail th, #article article .wrapper .right .detail .line .key, .common-reviews-container .elem, #category-front .main-head, .common-article-image-list .elem .image img
  { background-color: #2D2D2D !important; }
  #front .front-box .search-box .tabs a
  { background-color: #2D2D2D; }
  #front .front-box .search-box .tabs a.current, header #menubar .elem i, header #menubar .elem .main:hover, #front .front-box .search-box .tabs a:hover, #container .contents .head .mini-menu a i
  { background-color: #CCCCCC !important; }
  header #bar, .common-reviews-container .post .r .done, .common-reviews-container .elem .number
  { background-color: #3E3E3E !important; }
  footer #mainfooter .categories section .contents, #front .latest-review .body .cover, .common-comment-list .elem, #container .contents .body, #front .find-by-category .subcategory a.elem, header #bar nav .elem .main, #article article .wrapper, .common-article-image-list .elem, #category-front .latest-review .body .cover
  { background-color: transparent !important; }
  header #header .overlay, header #header, header #menubar, #front .front-box .search-box .tabs a.current, #front .front-box .search-box .tabs a, #front .front-box .search-box input, #front .article-box section .links a.label-gold, #front .article-box section .links a.label-silver, #front .article-box section .links a, .common-comment-list .elem, #front .latest-review .body .cover, footer #mainfooter, footer #mainfooter .categories section .contents, #container .contents .body, #front .find-by-category .subcategory a.elem, .common-button, #front .latest-request .elem .category span, #front .ranking .elem .context .detail .category, #front .article-box section .links a b, header #menubar .elem .main:hover, #front .front-box .search-box .tabs a:hover, header #bar .search input, header #bar nav .elem .main, #article article .wrapper, #article article .wrapper .left .panel, #article article .wrapper .right .detail .line .key, .common-reviews-container .elem, .common-reviews-container .post .r .done, .common-reviews-container .elem .number, .common-article-image-list .elem, #category-front .latest-review .body .cover
  { background-image: none !important; }
  .common-article-image-list .elem .image .time, .common-article-image-list .elem .image .cat
  { background-color: #000000 !important; opacity: 0.6 !important; }
  
  [color="#000"], [color="#000000"], [color="Black"], #container .contents .head p, .common-comment-list .elem p, #article article .wrapper .left .comment, .common-article-ranking-mini-list .elem .context .comment
  { color: #AAAAAA !important; }
  #front .article-box section h2 .detail p, #front .latest-bbs .elem .title a span, #article .common-breadcrumb, #article article .wrapper .right .detail .line .key, #article #right .reputation .detail .average, .common-reviews-container .elem .number, #category-front .main-head .common-breadcrumb, #category-front .active-user .elem .context .rank
  { color: #FFFFFF !important; }
  #front .front-box .search-box .tabs a.current, header #menubar .elem .main:hover, #front .front-box .search-box .tabs a:hover
  { color: #000000 !important; }
  #front .article-box section .links a b, #article #right .recommended .elem strong, .common-article-image-list .elem strong
  { color: #E0BC2D !important; }
  
  [color="#00F"], [color="#0000FF"], [color="Blue"], [color="Navy"], [color="#000080"], [color="#0000F0"], [color="#0000ee"], #front .article-box section h2 .detail p b, #front .latest-bbs .elem .date, #front .ranking .elem .context .detail .created, #front .latest-request .elem .date, footer #mainfooter .right .calendar .title, .common-reviews-container .elem .l .meta .date, .common-comment-list .elem .date, .common-article-image-list .elem .image .time
  { color: #3399FF !important; }
  .common-comment-list .elem .name, #front .latest-request .elem .type[type="new"], .common-username
  { color: #2FC95C !important; }
  #front .latest-request .elem .type[type="reup"]
  { color: #E3232A !important; }

  [style="float: right; width: 340px;"], .chat-container
  { display: none !important; }
  header #menubar, header #menubar .elem .main, header #menubar .last
  { border: 0 !important; }
  .common-article-image-list .elem .image, #category-front .common-article-image-list .elem
  { border: solid 1px #2D2D2D !important; }

  #article .common-breadcrumb, #category-front .main-head .common-breadcrumb
  { font-size: 14px !important; }

  body * { font-family: "苹方-简", "蘋方-簡", "蘋方-繁", "文泉驛微米黑", "WenQuanYi Micro Hei", "Droid Sans Fallback", "Noto Sans CJK TC Regular", "Microsoft YaHei UI", "Microsoft YaHei", "微软雅黑", Sans-serif; }
  body * { text-decoration: none !important; text-shadow: none !important; box-shadow: none !important; }
  html, body { color: #AAAAAA; background: #121212 !important; background-color: #121212 !important; background-image: none !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body ul, body li, body ol, body span, body p, body dt, body dd, body dl { background-color: transparent !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body th, body em, body b, body strong, label { color: #FFFFFF; }
  body div, body p  { color: #AAAAAA; }
  body blockquote { color: #AAAAAA !important; background-color: #2D2D2D !important; background-image: none !important; }
  body textarea { color: #E0BC2D !important; background-color: #2D2D2D !important; background-image: none !important; }
  body table, body tr, body td { background-color: transparent; color: #AAAAAA; background-image: none !important; }
  body select, body option { background-color: #2D2D2D !important; color: #E0BC2D !important; }
  body input { color: #E0BC2D !important; background-color: #2D2D2D !important; }
  body button, body input[type="button"], body input[type="submit"] { color: #000000 !important; background-color: #CCCCCC !important; }
  body a { color: #E0BC2D !important; }
  body a:link { color: #E0BC2D; }
  body a:visited { color: #AAAAAA; }
  body a:hover { color: #3399FF !important; }
  body a:active { color: #423F3F !important; }
  lightbox * { background: transparent !important; background-color: transparent !important; }
}
