css:
@-moz-document url("https://app.shopvox.com/app/#/jobboards/list"), url("https://app.shopvox.com"), url("https://app.shopvox.com/rapp/jobs/list") {
body {
 background-color: #494b4d;   
}
.button.on-dark {
 background-color: #8ec641;
}
body .root-ui-view>nav .main-nav, body>nav .main-nav {
   border-bottom: 1px solid #777;
}
body .root-ui-view>nav, body>nav {
    background-color: #777;
}
.walkme-icon-image-div walkme-launcher-image-div {
    display: none;
}
.body .root-ui-view>nav .main-nav>ul>li.active>a, body>nav .main-nav>ul>li.active>a {
 color: #8ec641;
}
.body .root-ui-view>nav .main-nav>ul>li>a, body>nav .main-nav>ul>li>a {
 color: #333;
    text-shadow: none
}
a{
 color: #333;
 font-weight: Bold;
}
.jobboard.list .main-section>.container, .jobboard.calendar .main-section>.container {
 padding: 0px;   
}
body .root-ui-view>section .main-section>header, body>section .main-section>header {
    min-height: 0px;
    line-height: 45px;
    margin-bottom: 0px;
}
td.company-name.ng-scope a {
    color: #333 !important;
    font-weight: normal;
}
td.stage.ng-scope {
    border-left: 1px solid #aaa;
}
.text--ellipsis-jobboard-list {

    display: inline-block;
    max-width: 110px;
    padding-top: 10px;
}
}

@-moz-document url-prefix("https://app.shopvox.com/rapp/jobs/list") {
.LayoutNavigation {
   
    border-bottom: 1px solid #444;
    background-color: #777;
    box-shadow: 0 0 5px rgba(0,0,0,.5);
   
}
.LayoutNavigationItem>.LayoutNavigationItem__wrapper {
    display: block;
    padding: 10px;
    text-decoration: none;
    color: #333;
    font-weight: bold;
    text-shadow: .25px .25px 0 rgba(0,0,0,.25);
}
.Button--primary {
    color: #fff;
    border-color: #76a634;
    background-color: #8ec641;
}
.Jobs__header {
    flex-shrink: 0;
    padding: 15px 10px;
    border-bottom: 1px solid #333;
    justify-content: space-between;
}
.Layout {
    position: relative;
    display: flex;
    overflow: hidden;
    flex-direction: column;
    width: 100vw;
    height: 100vh;
    background-color: #494b4d;
}
.VirtualTable .Table__cell.Table__cell--sorting-desc {
    border-top-color: #8ec641;
}
}
