css:
@-moz-document domain("wombathimmel.de"), domain("www.wombathimmel.de") {
body {
    color: #FFF;
}
body.custom-background {
    background: #0c0707;
}
#page {
    background: url(http://i.imgur.com/eHDie6E.gif) no-repeat -9999px -9999px;
}
input::-webkit-input-placeholder {
    color: #ffb8a7;
}
.site-header {
    background: radial-gradient(ellipse at center, #332727 0%, #1f1818 100%);
    box-shadow: 0 2px 20px black;
}
#logo {
    background-image: url(http://i.imgur.com/Nh5cnko.png);
    background-position: left center;
    background-repeat: no-repeat;
    background-size: 80px;
    padding-left: 100px;
    padding-top: 5px;
    line-height: normal;
}
#logo:hover,
#logo:active {
    background-image: url(http://i.imgur.com/eHDie6E.gif);
}
.primary-navigation-wrap {
    background: rgba(0, 0, 0, 0.25);
}
.sub-menu {
    background: #1a1414 !important;
}
.site-title {
    font-family: Acme;
}
.site-title a:hover,
.site-title a:active {
    color: #d53;
}
.site-description {
    font-family: Acme;
    word-spacing: 2px;
}
.menu-item a {
    font-family: Acme;
}
.type-post,
.type-page,
.type-attachment,
.comments-area,
.widget,
.page-header {
    background: radial-gradient(ellipse at center, #332727 0%, #1f1818 100%);
    box-shadow: inset 0 2px 2px #ff5e00;
}
.entry-title a:link,
.entry-title a:visited,
.entry-meta span:before {
    color: #d53;
}
.entry-meta a:link,
.entry-meta a:visited {
    color: #e4b082;
}
.entry-meta a:link:hover,
.entry-meta a:hover {
    color: #FFF;
}
.entry-title,
.widget-title,
.comment-reply-title span,
.page-title,
h1,
h2,
h3,
.page-header h1 {
    font-family: Acme;
}
.entry-meta .meta-date, .entry-meta .meta-author, .entry-meta .meta-category, .entry-meta .meta-comments {
        display: inline-block;
}
a.more-link {
    color: #FFF !important;
}
.more-link:hover,
.more-link:active {
    background: #ef9e36;
}
a:hover,
a:focus,
a:active {
    color: #e4b082;
}
div.pp_pic_holder {
    box-shadow: 0 2px 20px black;
    outline: 3px solid #ff8a25;
}
/* latin */

@font-face {
    font-family: 'Acme';
    font-style: normal;
    font-weight: 400;
    src: local('Acme Regular'), local('Acme-Regular'), url(https://fonts.gstatic.com/s/acme/v6/CMfjQTCaAjBuWy3e4bQMZA.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
/* latin */

@font-face {
    font-family: 'Ubuntu';
    font-style: normal;
    font-weight: 400;
    src: local('Ubuntu'), url(https://fonts.gstatic.com/s/ubuntu/v9/sDGTilo5QRsfWu6Yc11AXg.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
/* latin-ext */

@font-face {
    font-family: 'Ubuntu';
    font-style: normal;
    font-weight: 400;
    src: local('Ubuntu'), url(https://fonts.gstatic.com/s/ubuntu/v9/Wu5Iuha-XnKDBvqRwQzAG_esZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
    unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
}
