css:
/* i really want this to be global */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tribalwars.nl") {

  body 
  {
    background:none!important;
    background-color:#36393E!important;
    font-weight: 550!important;
    font-size:13px!important;
  }

  
  th, .vis > h4 
  {
background: #40454c!important;/* Old browsers */
background: -moz-linear-gradient(top, #40454c 0%, #1e2124 100%, #207cca 100%, #2989d8 100%)!important; /* FF3.6-15 */
background: -webkit-linear-gradient(top, #40454c 0%,#1e2124 100%,#207cca 100%,#2989d8 100%)!important; /* Chrome10-25,Safari5.1-6 */
background: linear-gradient(to bottom, #40454c 0%,#1e2124 100%,#207cca 100%,#2989d8 100%)!important;
    
   /* blue version 
    background: #7f9bf2!important; 
    background: -moz-linear-gradient(top, #7f9bf2 0%, #6b80ca 100%, #6b80ca 100%, #6b80ca 100%)!important; 
    background: -webkit-linear-gradient(top, #7f9bf2 0%,#6b80ca 100%,#6b80ca 100%,#6b80ca 100%)!important; 
    background: linear-gradient(to bottom, #7f9bf2 0%,#6b80ca 100%,#6b80ca 100%,#6b80ca 100%)!important; */
    color:#FFF!important;
  }
  
  .forum 
  {
    border:0!important;
    background:rgba(62, 65, 70, 0.4)!important;
  }
  
  .selected
  {
    border:0!important;
    background:#6B80CA!important;
  }
    
 td 
  {
    color:#FFF!important;
  }
  
  .text 
  {
    background:rgba(62, 65, 70, 0.7) !important; 
  }
  
  .post
  {
    border:0!important;  
    background:rgba(62, 65, 70, 0.7) !important; 
  }
  
  .igmline
  {
   background: linear-gradient(to bottom, #40454c 0%,#1e2124 100%,#207cca 100%,#2989d8 100%) !important;
   border:0!important;
   color: #FFF !important; 
  }
  
  }
  .forum-content
  {
    background: transparent!important;
    border:0!important;
  }
  
  .vis 
  {
    border:0!important;
  }
  .header-border .box 
    {
       background:none!important;
    }
  /*DISPLAY NONE*/
  .bg_left 
  {
    display:none!important;
  }
  .bg_right
  {
    display:none!important;
  }
  
  /*MIDDLE*/
  .maincell 
  {
    background:none!important;
    padding:10px!important;
    background-color:#2F3136!important;
  }

  .content-border
  {
    background:none!important;
    border:0!important;
    -webkit-box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0.0)!important;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0.0)!important;
  }
  
  #inner-border
  {
    border:0!important;
  }
  
  .server_info {
    font-size: 11pt!important;
    margin-bottom: 0!important;
    color: #514d41;
    margin: 4px 3px -10px 0;
    background:transparent!important;
   }
  
  .bg_bottomleft, .bg_bottomright, .bg_bottomcenter
  {
    background:none!important;
  }
  /*MENU*/
  .menu-item 
  {
    background:none!important;
  }
  
  #menu_row > td.menu-item > a 
  {
    background:none!important;
    font-size:0!important;
  }
  td.menu-item
  {
    height:30px!important;
    width:30px!important;
    border-radius:50px!important;
    background-color:#2E3136!important;
  }

  .buttonicon > a:nth-child
  {
    display: block!important;
    margin-left: auto!important;
    margin-right: auto!important;
  }

  td.menu-item:nth-child(10)
  {
    min-width:45px!important;
  }
  
  #menu_row > td:nth-child(6) 
  {
    width:0px!important;
  }
  
  td.menu-item:nth-child(2)
  {
    background-color:#6B80CA!important;
  }
  
   #tribe_forum_indicator 
  {
    position:relative!important;
    left: 10px!important;
  }
  
  #new_mail
  {
    position:relative!important;
    left: 3px!important;
  }
  
  td.menu-item img
  {
    display:block!important;
    margin:auto!important;
  }
  
  #topdisplay .bg 
  {
    background:none!important;
  }
  
  #topTable
  {
    background:none!important;
    margin-top:7px!important;
  }
 
   .top_bar 
  {
    background:none!important;   
    background-color:#1E2124!important;
    height:66px!important;
    box-shadow: 0px 3px 2px -2px rgba(37,39,43,1)!important;
  }

  .top_bar .bg_left 
  { 
    background:none!important;   
  }
  
  .top_bar .bg_right
  {
    background:none!important;   
  }
  
  .top_shadow 
  {
    background:none!important;   
  }
  
  /*MENUBAR*/
  .icon-box 
  {
    background:none!important;
    padding-left: 3px;
  }
  
  .res
  {
    color: #FFF!important;
  }
  
  .warn_90 
  {
    color: #F04747!important;
  }
  
  .header-border 
  {
    background:none!important;
  }
  
  .top, .right, .left, .main, .bottomborder, .topborder, .bottomborder
  {
   background:none!important;
  }
 
  .order-progress > div:nth-child(2)
  {
     background-color: rgba(67, 181, 129, 0.9)!important;
    
  }
  .menu_column 
  {
    padding-top:11px!important;
    margin-left:-10px!important;
  }
  .topbar .corner, .menu_column, .decoration {
    background:none!important;
}
  .menu_column .menu-column-item 
  {
    background:#2E3136!important;
  }
  
  #pop_max_label, #storage, .nowrap
  {
    color:#FFF!important;
  }
   .menu_column .menu-column-item a:hover
  {
    background:#282B30!important;
  }
  .menu_column .menu-column-item a {
    background:none!important;
    font-size:11px!important;
  }
  column-item a, .menu_column .menu-column-item 
  {
    background:#2E3136!important;
    padding-left:15px!important;
    padding-bottom:8px!important;
  }
  .flag_box 
  {
    border: 0!important;
    background-color: transparent!important;
    -webkit-box-shadow: 1px 1px 3px 0px rgba(0, 0, 0, 0.25);
    box-shadow: 1px 1px 3px 0px rgba(0, 0, 0, 0.25);
  }
  
  [class*="flag"] span
  {
    color:#FFF!important;
    border:0!important;
    background:none!important;
    background-color:#36393E!important;
  }
  .popup_box 
  {
  border:2px black solid!important;
  border-radius:10px!important;
  }
  
  .popup_box_close 
  {
    background: gray!important;
  }
    .popup_box_close:after
  {
    content:"X";
    padding-left:1px!important;
  }
  
  .popup_box_content
  {
    background:none!important;
    background-color: rgba(62, 65, 70, 0.96)!important;
    padding-left:10px!important;
    padding-top:20px!important;
  }
  .progress-bar .label
  {
    color:#FFF!important;
  }
  .progress-bar 
  {
    background-color:#36393E!important;
  }
  #plunder_list_filters 
  {
    background-color:#fff!important;
  }
  
  #plunder_list_filters
  {
    background-color:transparent!important;
    color:white!important;
  }

  td.lit-item:nth-child(1),  td.lit-item:nth-child(2),  td.lit-item:nth-child(3),  td.lit-item:nth-child(4),  td.lit-item:nth-child(5)
  {
    background:transparent!important;
    color:white!important;   
  }
 
  
  .anim, .order-progress, .order-progress > div:nth-child(1), .order-progress > div:nth-child(2)
  {
    height:8px!important;
    z-index:-0!important;
  }
  
  .order-progress 
  {
    position:relative!important;
    border:0!important;
  }
  
  .vis td, .vis_item
  {
    background:none!important;
    border-bottom: 1px #3E4146 solid;
    background-color: rgba(62, 65, 70, 0.7)!important;
    height:25px!important;
    color:#C0C1C2!important;
  }
  /*COLOR*/
  a 
  {
   color:#FFF!important;
  }
  
  a:hover 
  {
   background-color:#282B30!important;
  }

  .inactive
  {
  color:#626568!important;
  }
  
  .map_legend
  {
    width:200px!important;  
  }
  
  .map-legend-container table 
  {
    border: 0!important;
    background-color:#36393E!important;
    padding:10px!important;
  }
  
  .map_navigation, #map_whole > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1), #map_whole > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(3)
  {
    display:none!important;
  }
  .map_container 
  {
    border:0!important;
  }
  #pop_current_label
  {
    color:#FFF!important;
  }
  
  
  .btn
  {
       background:none!important;
   background: linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
   border:0!important;
   border-radius:3px!important;
  }
  
    .btn-bcr {
   background:none!important;
   background: url("https://dsnl.innogamescdn.com/8.85/33332/graphic/btn/buttons.png") no-repeat 3px -24px, linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
    border-radius:3px!important;
  }
  
  .btn-cancel 
  {
   background:none!important;
   background: url("https://dsnl.innogamescdn.com/8.85/33332/graphic/btn/buttons.png") no-repeat 3px -174px, linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
   border:0!important;
   border-radius:3px!important;
  }
  
  .btn-build {
   background:none!important;
   background: url("https://dsnl.innogamescdn.com/8.85/33332/graphic/btn/buttons.png") no-repeat 3px 1px, linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
   border:0!important;
   border-radius:3px!important;
   }
  
   .btn-btr {
   background:none!important;
   background: url("https://dsnl.innogamescdn.com/8.85/33332/graphic/btn/buttons.png") no-repeat 3px -49px, linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
   border:0!important;
   border-radius:3px!important;
   }

  .btn-attack
  {
   background:none!important;
   background: url("https://dsnl.innogamescdn.com/8.85/33332/graphic/btn/buttons.png") no-repeat 3px -74px, linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
   border:0!important;
   border-radius:3px!important;
  }
  
  .btn-support
  {
   background:none!important;
   background: url("https://dsnl.innogamescdn.com/8.85/33332/graphic/btn/buttons.png") no-repeat 3px -99px, linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
   border:0!important;
   border-radius:3px!important;
  }

  
  input[type="text"], input[type="password"], input[type="email"]  
  {
    background:#2A2D31!important;
    border:1px #1D1F22 solid!important;
    border-radius:2px!important;
    color:#FFF!important;
  }
  
  /*H*/
  h1, h2, h3, h4
  {
   color:#FFF!important; 
  }
  p 
  {
   color:#C0C1C2!important;   
  }
 
   #content_value > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2)
  {
   color:#C0C1C2!important;   
  }
  #content_value > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2)
  {
    color:#C0C1C2!important;  
}
