css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ohmydollz.com") {
  
   #contenu {background: url("http://mysteriaobscura.free.fr/Forum/Fond.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0) !important; height: 100%; margin: 0; padding: 3px 0; width: 100%;}

/*HEADER*/

  #head_forum {background-image: url("http://mysteriaobscura.free.fr/Forum/Banniere.png"); background-repeat: no-repeat; height: 300px; position: relative; width: 950px;}

  #head_links {bottom: 18px; left: 150px; position: absolute;}

  #head_links a {color: #CCCCCC; font-size: 14px; text-decoration: none;}

  #head_links a:hover {color: #CCCCCC;}

  #brdmenu {background-image: url("http://mysteriaobscura.free.fr/Forum/Titres.png"); background-repeat: no-repeat; height: 30px;}

  #footer_box {background-image: url("http://mysteriaobscura.free.fr/Forum/Sous-titres.png"); background-repeat: no-repeat; height: 100px; width: 950px;}

  #footer_box p, #footer_box a {color: #CCCCCC; font-weight: bold; margin-top: 45px; padding-right: 12px; right: 0;}

  #brdwelcome {background-image: url("http://mysteriaobscura.free.fr/Forum/Connexion.png"); background-repeat: no-repeat; color: #CCCCCC; font-weight: bold; height: 60px; padding: 10px; width: 950px;}

  .pun H2, #brdmenu {background-image: url("http://mysteriaobscura.free.fr/Forum/Titres.png"); background-repeat: no-repeat;border-top-left-radius: 5px; border-top-right-radius: 5px;}

  .pun {color: #CCCCCC;}

  DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {background-color: #151515;}

  .pun BLOCKQUOTE {border: 1px solid #151515;}

  .box {border: 1px solid #151515;}

  .pun A:link, .pun A:visited {color: #CCCCCC;}

  .pun A:hover {color: #CCCCCC;}

  DIV.block DIV.inbox, DIV.blockmenu DIV.inbox {padding: 0 6px; color: #CCCCCC;}

  .pun TH {color: #CCCCCC; background: url("http://mysteriaobscura.free.fr/Forum/Sous-titres.png");}

  .inbox tr {border: 1px solid #CCCCCC;}

  pun .tcr .byuser {color: #CCCCCC;}

  TD.tc2, TD.tc3, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo, #adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD {background-color: #151515;}

  TD DIV.tclcon {color: #CCCCCC; float: left; margin-left: 10px; width: 475px;}

  .pun .tcr .byuser {color: #CCCCCC;}

  .pun .tcr {border-right: 1px solid #CCCCCC;}

  DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {background-color: #CCCCCC;}

  DIV.blockpost DIV.box {background-color: #151515;}

  DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {background-color: #151515; color: #B676D1;}

  DIV.postright, DIV.postfootright {background-color: #151515;}

  .pun BLOCKQUOTE {background-color: #B676D1;}

  DIV.postright, DIV.postfootright {border-left: 18em solid #151515;}

  DIV.postleft DT, DIV.postmsg H4, TD.tcl H3, DIV.forminfo H3, P.postlink, DIV.linkst LI, DIV.linksb LI, DIV.postlinksb LI, .blockmenu LI, #brdtitle H1, .pun SPAN.warntext, .pun P.warntext {font-weight: bold; color: #CCCCCC;}

  DIV.icon {background-image: url("http://mysteriaobscura.free.fr/Forum/Lu.png") !important;}

  DIV.inew {background-image: url("http://mysteriaobscura.free.fr/Forum/Nouveau.png") !important;}

  DIV.blockpost H2 SPAN.conr {color: #CCCCCC; text-align: right;}

  .pun H2 A:link, .pun H2 A:visited {color: #CCCCCC;}

  DIV.linkst .conl, DIV.linksb .conl, DIV.postlinksb .conl {width: 18em; color: #CCCCCC;}
  
  .postleft dt a {color:#FFF !important;}

}
