css:
@-moz-document domain("intranet.cpnv.ch") {
body{
	background-color:#222;
    background-image:none;
}
#intranet_header .intranet-inner,
#intranet_middle .intranet-inner,
.intranet-firstline-item{
	background-image:none;
    background-color:#333;
    color:#eee;
}
#intranet_topforms_search,
#intranet_topforms_context,
#intranet_breadcrumb .intranet-inner,
#intranet_wide,
#intranet_fluid{
    background-image:none;
    background-color:#444;
}
.fc-button{
	filter:invert(.8) saturate(0)
}
.intranet-firstline-item{
	box-shadow:none;
    border-radius:2px;
}
#sidemenu_button{
	color:#eee;
}
#intranet_content{
    padding-top:10px;
}
#sidemenu{
    margin-top:10px;
}
#intranet_middle,#intranet_breadcrumb,#intranet_footer,
#intranet_footer .intranet-inner{
    background-image:none;
}
.simple-box{
	box-shadow:none;
    background-color:#444;
    border-radius:2px;
    
}

ul.contextmenu li a{
	color:#eee;
    background-color:transparent;
    border-top: 1px solid #555
}
ul.contextmenu li a:hover{
	background-color:rgba(0,0,0,.2);
}
#intranet_middle h3{
	background-color: #396377;
	border-radius:2px;
}
input#direct_rid_flexselect {
  background-color: transparent;
  color: #ccc;
  border: 0;
}

#intranet_footer .intranet-inner {
  background-color: #2d2d2d;
  border: 1px solid #555;
  margin-left: 35px;
  border-top: 0px;
}
.inner-box{
	border:0px;
    border-radius:2px;
    background-color:#444;
}
form.formtastic.boxed {
  color: #5890ab;
}
input {
  background-color: transparent;
  border: 0px;
  border-bottom: 1px solid #ddd;
  color: #ddd;
}
div.error {
  background-color: #9c5d5d;
  border: solid 1px #8c5353;
  border-radius: 2px;
}
.notice{
  background-color: #4a7751;
  border: solid 1px #445d49;
}
.fc-widget-header, .fc-widget-content {
  border: 1px solid #666;
}
.fc-widget-header .fc-agenda-divider-inner {
  background: #666;
}
.tag-list li a{
  background-color: #666;
  color: #eee;
}

.green-box{
    background-color:#555;
    border-radius:2px;
    color:#7cbede;
}
#intranet_middle h4{
    color:#7cbede;
    border-color:#7cbede;
}

#search_form ul li div.selected {
  border: 0;
  background-color: #444;
}

#search_form ul li div {
  border: 0;
  background-color: #666;
}

table.resource{
	background-color:transparent;
}
table.resource th{
	background-color:rgba(255,255,255,.1);
}
table.resource th a{
    color:#eee;
}
table.resource tr.record{
	background-color:transparent;
}
table.resource tr.even-record{
	background-color:rgba(255,255,255,.1);
}
.resource-grid .grid-item{
	background-color: #555;
    border: 0px;
    border-radius:2px;
}
.grid-item span{
	color: #6195af;
}
#sidebar .dragbox{
  border-radius: 2px;
  border-color:#333;
}

.sidebar-content{
	background-color:#555;
}
#sidebar{
	margin-top:10px;
}

.content-box{
	box-shadow:none;
    background-color:#444;
    border-radius:2px;
    border:0px;
}
th{
	background-color:#555;
    border-color:#555;
}
td{
	background-color: #444;
    color: #eee;
}
.colheadyear{
    background-color:#555;
}
table, tr, td {
  border-color:#555;
}
table{
	border: 1px solid #555 !important;
}
tr:nth-child(2n+1){
	background-color:#555;
}

form.formtastic.boxed.simple-box .field-box {
  background-color: #555;
  border-radius: 2px;
  box-shadow:none;
  color:#7cbede;
}
.card dt{
  color: #7cbede;
}
td.moduleInstancecell[style="background-color:#ccc"] {
    background-color: rgba(255,255,255,.1) !important;
}
.colhead {
  background-color: #555;
  border-color :#666;
}
div .moment .fc-event-skin,div .moment.fc-event-skin {
  background-color: #775992;
  border-color: #775992;
  color: #eee;
}
.fc-state-highlight {
  background-color: rgba(0,0,0,.3);
}
table.resource tr.record td:first-child {
  background-color: #555;
}
table.resource.absences tr.record:hover, table.resource tr.record:hover td:first-child, table.resource tr.even-record:hover td:first-child {
  background-color: #444;
}

}
