css:
@-moz-document domain("bergames.com") {

/*Header*/

#header-search input.search-field, #header-search button {
    border-color: #848484;
    height: 1.88rem;
    line-height: 1.88;
    width: 130px;
}

#header-search button {
    background: black;
    border-radius: 0 3px 3px 0;
    color: transparent;
    float: left;
    overflow: hidden;
    position: relative;
    -webkit-transition: background 90ms ease-in-out;
    transition: background 90ms ease-in-out;
    width: 32px;
}

/*Main*/

div.site-header-main {
    color: #fff;
    padding: 23px 0;
    background: linear-gradient(to bottom, rgba(96,12,192,1) 0%,rgba(14,0,28,1) 100%) !important;
}

#content {
    /* background-color: #212121; */
    min-height: 23em;
    padding-bottom: 30px;
    padding-top: 30px;
    background: linear-gradient(to bottom, rgba(206,206,206,1) 0%,rgba(10,10,10,1) 100%);
}

body{
    color: #d6d6d6;
    font-family: "Source Sans Pro", sans-serif;
    font-size: 0.875rem;
    line-height: 1.65;
    background-color: #8a8a8a;
}

/*Twitter Stuff*/

.timeline-Widget {
    max-width: 1200px;
    background-color: #797979;
    border-radius: 4px;
}

.timeline-Header-title {
    font-size: 21px;
    font-weight: 300;
    line-height: 24px;
    color: #292F33;
}

a:visited {
    color: #FFFFFF !important;
    text-decoration: none;
    outline: 0;
}

a {
    color: #e5e5e5 !important;
    text-decoration: none;
    outline: 0;
}

/*WE'RE GETTING INTO FORUMS*/

a.bbp-forum-title {
    color: #b24cff !important;
    font-weight: bold;
    font-size: 125%;
}

body {
    color: #565656 !important;
    font-family: "Source Sans Pro", sans-serif;
    font-size: 0.875rem;
    line-height: 1.65;
}

#bbpress-forums li.bbp-header {
    background: #5d5d5d;
}

.entry-header .entry-title {
    color: #303030;
    font-size: 2.5em;
    margin: 0;
    word-wrap: break-word;
}

.page-content a, .entry-content a, .comment-content a, .entry-summary a {
    border-bottom: dotted 1px #aaa;
    color: #808080;
    text-decoration: none;
}

body.custom-background {
    background-color: #b2b2b2;
    background-image: url(http://i.imgur.com/KsnSWGZ.png);
    background-position: left top;
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
}

#bbpress-forums div.odd, #bbpress-forums ul.odd {
    background-color: #8f8f8f;
}

#bbpress-forums div.even, #bbpress-forums ul.even {
    background-color: #a0a0a0;
}

#bbpress-forums li.bbp-header, #bbpress-forums li.bbp-footer {
    background: #6d6d6d;
    border-top: 1px solid #eee;
    font-weight: bold;
    padding: 8px;
    text-align: center;
}

fieldset {
    border: 2px solid #a139d8 !important;
    background-color: #8a8a8a !important;
}

div.mce-panel {
    border: 0;
    background: #525252;
}


}
