css:
@-moz-document url-prefix("https://www.letras.mus.br/"), regexp("https://www.letras.com.br/") {
body {
    background: #202020;
}
.main-header {
    background: #161616!important;
}
.main-search_input {
    color: #ccc;
    background: #161616;
}
.cnt-letra p, .cnt-trad p {
    color:#ccc;
}
article, aside, footer, header, nav, section {
    background: #202020;
}
div#js-lyric-cnt {
    background: #202020;
}
div#cnt_top {
    background: #202020;
}
#pub_10 {
    height: 0px;
    margin-bottom: 25px;
    display: none;
}
.cnt-filter_more>a, .cnt-filter_tabs>a {
    color: #ccc;
}
.ad-full {
    display: none;
}
.cnt-head--l {
    margin-top: 25px;
}

/* pagina banda */
.h-sep {
    display: none;
}
.cnt-list a {
    color: #ccc;
}
.hr {
    border-bottom: 0px solid #EEE;
}
.h-link {
    color: #ccc;
}
.cnt-filter_form input {
    border-color: #171717;
    background: #171717;
}
.cnt-filter input {
    color: #ccc;
    border: 0px solid #CCC;
}
.bt-fa.on {
    background: #171717;
    border-color: #171717;
}
.cnt-filter_more>a.on, .cnt-filter_tabs>a.on {
    background: #171717;
}
ins.dcmads {
    display: none!important;
}

}
