@-moz-document domain("qwant.com") {




/* ------------- Fond ------------- */  


body, .sidebar, .ribbon_info--container, .results-column--images h2,
.results-page h2, .form_page, .footer, .search_bar .search_bar__form__input,
.movie_st__info, .results-column--images-ribbon .result_fragment .result .result__thumb-container,
.sidebar__top, .trends--general__trend--center .trends--general__info, .results-column--videos .result,
.boards_page, .board_page, .note-detail, .sidebar__info, .sidebar__statistics,
.board-page__sidebar__subcontainer, .header, .board, div.boards__filter, div.category_menu__list--boards,
.notes_page, .notes-list .note, .category_menu__list--notes, .profile-head, .show_profile,
.ribbon-knowledge--container, .ribbon_images, .suggester, .alert__item__text,
.alert__item--success .icon, .alert__item__close, .favorites .favorites__background,
.select_region .country, .select_region .country__language,
.site-header, .site-header-menu, .header_inner, .home-banner, .search_bar__form, .result-list__search-holder,
.ribbon_knowledge_discover, .result-type__news__text--container, .result_filters_container__box, .result__social__item,
.result.result--images, .appmenu__background, appmenu__content, .appmenu__content--main_menu, .wrapper
{
  background:url('data:image/png;base64,')!important;
}

/* ------------- Nom Utilisateur ------------- */
.header__user__name:hover
{
    color: #F2F200!important;
}

/* ==================== LIENS ==================== */

/* ------------- Résultats ------------- */
.result--web--title
{
    color: #3E72BF !important;
}
a.result--web--link:hover, .result--web--title:hover
{
    text-decoration: none !important;
}

/* ------------- Résultats survolés ------------- */
.result--web--title:hover
{
    color: #51A4F7 !important;
}
/* ------------- Résultats visités ------------- */
.result--web--link:visited .result--web--title
{
    color: #805555!important;
}
/* ------------- Résultats visités survolés ---------- */
.result--web--link:visited:hover .result--web--title
{
    color: #A37474!important;
}


/* ------------- Boutons Liens ------------- */
.result_actions__circle
{
    fill: #232432;
}
.result_actions__circle:hover
{
    fill: #282939;
}

/* ------------- Liens ------------- */
.result__url
{
  font-size: 13.5px;
  color: #3D764B;
}

/* ------------- Descriptifs ------------- */
p
{
  color: #7c7c7c!important;
}

/* ------------- Bandeau de bienvenue ------------- */
.alert__item
{
  border: 1px solid #A45200!important;
}

/* ---------- Texte Recherche Saisi -------------- */
.search_bar__form__input
{
  color: #5C6F84 !important;
}

/* ------------- Suggestions de recherche ------------- */

.suggester__list__element.suggester__list__element--standard
{
  color:#72767A!important;
}
b:hover
{
  color: #51A4F7!important;
}

/* ------------- Titre Résumé recherche ------------- */
h3.ribbon_info__title
{
  color:#78753D!important;
}
/* ------------- Texte Résumé recherche ------------- */
.ribbon_info__description
{
  color: #7c7c7c!important;
}
/* ------------- Photos Résumé recherche ------------- */
.ia_container--vertical .ribbon_images
{
  border: 2px solid #40435E;
}


/* ------------- Liens vers Résumé Recherche ------------- */
span.ribbon_info__url__text
{
  color:#3D764B!important;
}
span.ribbon_info__url__text:hover
{
  color:#5AA96F!important;
} 

/* ============================= Barre latérale gauche ========================= */

/* ------------- catégories ------------- */
.result_filters_container__filters .result_filter__item__label
{
  color:#C46200!important;
  font-size: 1.07em;
}
/* ------------- sélectionné ------------- */
.result_filters_container__filters .wrapper-list li a.selected
{
  color:#78753D!important;
  font-weight: bold;
}
/* ------------- liste ------------- */
.result_filters_container__filters .wrapper-list li a
{
  color:#787A87!important;
  font-weight: normal;
} 

/* ------------- Menu IMAGES / VIDEOS ------------- */
.wrapper-dropdown .dropdown
{
  background:#2C2C3F!important;
  box-shadow: 0 1px 4px #888;
  border-radius: 1px;
}
.dropdown__item
{
  color: #707378!important;
  font-size: 16px;
}
.wrapper-dropdown.active
{
  background: #506470!important;
}
 


/* ------------- Logo ------------- */
.home__logo
{
  background: url("http://woam.magix.net/public/images/qwantlogoone.png") no-repeat;
}

/* ------------- Toutes les actualités ------------- */
.trends--big-text__more-news
{
  color:#3D764B!important;
}


/* =============================== Suppressions : ======================== */

/* ------------- >>> Invite savoir plus ------------- */
div.header__text
{
  display: none!important;
}
/* ------------- >>> résultats sociaux ------------- */
div.result.result--social
{
  display: none!important;
}

/* ------------- >>> Bas de Page ------------- */
.footer
{
  display: none!important;
}

/* ------------- >>> Popup Extension Qwant ------------- */ 
.home__snippet__extension--container
{
  display:none!important;
}
.html
{
  padding-bottom: 0px;
} 
.snippet
{
  display: none!important;
}

/* ------------- >>> Logos partenaires musicaux ------------- */
.home__music__partners
{
  display: none!important;
}

/* ------------- >>> Votre avis nous intéresse... ------------- */
.survey__popin
{
  display: none!important;
}



/* ======================================================= */ 


/* ------------- Ligne séparation résultats ------------- */
.result_fragment
{
  border-top: 0px solid #40435E!important;
}


/* ------------- Encart Film + visionneuse ------------- */
.movie_st__info
{
  color: #3D764B!important;
  border: 3px#40435E !important;
}
.movie_st__trailer
{
  border: 3px solid #40435E !important;
  border-bottom: 0px solid #E17100!important;
}
.movie_st__info h1
{
  color: #78753d!important;
}
.movie_st__grade
{
  color: #5B5B5B;
}


/* ------------- Texte page Actu ------------- */
.news_block__desc
{
  font-size: 14px;
  color: #7c7c7c;
}
.news_list_item__desc
{
  font-size: 14px;
  color: #7c7c7c;
}

/* ------------- cadre commentaires actu ------------- */
.trends--general__trend--center .trends--general__info
{
  border-top: 3px solid #40435E!important;
  border: 1px solid #40435e;
}

/* ------------- cadre commentaires vidéos ------------- */
.results-column--videos .result
{
  border: 1px solid #40435e;
}

/*
.trends--social__link__normal {
color: #7c7c7c;
opacity: 0.98;
}
*/

/* ------------- Sélection catégories ------------- */
.results-page--all .ribbon__tabs__tab.ribbon__tabs__tab--selected
{
  color: #F2F200;
  font-weight: normal;
  border-bottom: 3px solid #3cbe64;
}
/* ------------- Titres Page Tous ------------- */
.results-page h2 a
{
  color: #8E8B48!important;
}
.sidebar__top
{
  color:#E17100!important;
}

/* ======================== Profil Utilisateur ====================== */
/* ------------- Titre ------------- */
.form_page h1
{
  font-size: 34px;
  color:#8E8B48!important;
}

/* ------------- >>> lignes séparation ------------- */
.account_page hr
{
  border-top: 1px solid #40435e!important;
}

/* ------------- Texte saisi ------------- */
.input__text,.input__text:active, .input__text:focus,
.input__textarea:active, .input__textarea:focus
{
  border-color: #40435e;
  color:#8E8B48!important;
} 

/* ------------- >>> Boutons ------------- */
.button--blue
{
  background-color: #2E3043;
  color:#9E9E9E!important;
}
.button--blue:hover
{
  background-color: #474967;
} 
.button--grey
{
  background-color: #8E8B48;
  color: #000000;
}
.button--grey:hover
{
  background-color: #FF8040;
  color: #000000;
}

/* ------------- champ Citation ------------- */
.account_page .account_page__fields .input__textarea
{
  background-color: #1F212D;
}

/* ------------- je veux souscrire à la newsletter ------------- */
.toggle-switch
{
  color: #3D764B!important;
}

/* ------------- Région de recherche ------------- */
.select_region .country, .select_region .country__language
{
  border:1px solid #303245!important;
  border-radius: 30px;
  box-shadow: 0 1px 2px rgba(0,0,0,0.08);
  font-size: 15px;
  color: #72767A;
}
.select_region .region__continents__continent--selected
{
  border-left: 3px solid #FFFF00;
  color: #FFFF00;
}


/* ================== CARNETS =========================== */ 

/* ------------- Champ recherche  ------------- */
.search__input
{
  background-color: #1F212D;
  color:#7c7c7c!important;
  border:1px solid #40435e!important;
}

/* ------------- Bords des widget  ------------- */
.board, div.boards__filter, div.category_menu__list--boards
{
  border:1px solid #40435e!important;
  border-radius: 15px;
}
.board
{
  border-radius: 15px;
  border-bottom: 1px solid #cacacc;
}
.sidebar__info
{
  border:1px solid #40435e!important;
}
.sidebar__embed .word-wrap--cp
{
  background-color: #CACACC;
}
.sidebar__statistics__stats__specific
{
  color:#786969!important;
}
.sidebar__statistics__followers .button
{
  background: #42445E;
}
.sidebar__statistics__followers .button:hover
{
  background: #72767A;
}
.button--greywhite
{
  border: 1px solid #40435e;
  color: #000000;
}
.note-detail__title a:active, .note-detail__title a:hover,
.note-detail__title a:link, .note-detail__title a:visited
{
  color: #8e8b48;
}

/* ------------- Réduction Bas de page ------------- */
.wrapper
{
  min-height: 100vh;
  padding-bottom: 0px;
}

/* ------------- Cadre image centrale ------------- */
.boards__jumbotron
{
  border:2px solid #40435e!important;
}

/* ------------- Titres catégories barre de droite ------------- */
.category_menu__list__title, .boards__filter__label
{
  font-size: 14px;
  color: #17802A!important;
  text-transform: uppercase;
}

/* ------------- catégories survolées ------------- */
.category_menu__list__item__label:hover
{
  color: #17802A;
}

/* ------------- catégorie & icônes sélectionnée ------------- */
.category_menu__list__item--active a
{
  color: #D96C00!important;
}
.category_menu__list__item--active a:hover
{
  color: #D96C00!important;
}
.category_menu__list--boards .icon-category:hover
{
  color: #17802A!important;
}


/* ===================== MES CARNETS ============================= */ 

.board--plus .board--plus--text
{
  font-size: 19px;
  text-shadow: 0 1px 0 #1C1D29;
}
h1
{
  color:#17802A!important;
}
.note-detail
{
  border:1px solid #40435e!important;
  border-radius: 4px;
  color: #393b3d;
}


/* ========================= NOTES ================================ */

/* ------------- cadres ------------- */
.notes-list .note
{
  border:1px solid #40435e!important;
  border-radius: 15px;
}

/* ------------- suppression bords supérieurs colorés ------------- */
.notes-list .note--url .note__text,
.notes-list .note--text .note__main-link,
.notes-list .note--video .note__main-link,
.notes-list .note--image .note__main-link
{
  border-top: 0px!important;
}

/* ------------- Flèches Carroussel ------------- */
.trends--people__left, .trends--people__right
{
  color: #575B82;
}

/* ------------- Menu informations Qwant ------------- */ 
.site-header-menu a
{
  color: #40435e!important; 
}
.site-header-menu a:hover
{
  color: #575B82!important; 
}
.header_inner
{
  border-bottom: 1px solid #40435e;
}

/* ------------- Suppression boutons 'Extension' & 'Partager' Qwant ------------- */
.home__snippet__extension, .header__share-qwant
{
  display: none!important;
}

/* ------------- Rotation Icone searchbar ------------- */
.search_bar__form__logo
{
    transition: all .5s!important;
}
.search_bar__form__logo:hover
{
    transform: rotate(2turn)!important;
}

/* ================= V4 ======================== */

/* ---- Barre de Recherche ---- */
.search_bar__form, .suggester
{
    border: 1px solid #5C6F84 !important;
    /*box-shadow: 0px 1px 5px 3px inset black !important;*/
}
/* ---- Suggestions de Recherche ---- */
.suggester li:hover
{
    background: #242535 !important;
}

/* ---- Logo QWANT ---- */
canvas, .verticals__container, 
.trends--general__info__text--brand.trends--general__info__text--brand--custom
{
    display: none !important;
}
.home__logo__container.home__logo__container--default
{      
  background: url("http://woam.magix.net/public/images/qwantlogoone.png") no-repeat !important;
  height: 100px;
  position:absolute !important;
  left: 50%;
  transform: translateX(-25%);
  top:3%;
}
 .home__logo, .home__baseline
{
    opacity:0;
} 
 
/* ---- Bouton Connexion/Options ---- */
.header__item.header__item--appmenu
{
    opacity: 0.1 !important;
    transform: scale(0.7);
    transition: all 0.5s;
}
.header__item.header__item--appmenu:hover
{
    opacity: 0.4 !important;
    animation: pulse 0.5s;
}

/* ----- Cadre ia info ----- */
.ia_container-web_desktop
{
    border: 1px solid #474A4E !important;
}
h2
{
    border-bottom: 2px solid #474A4E !important;
}

/* ----- no smart results on results page ----- */
.result-smart__item.result-smart__news, .result-smart__item.result-smart__social
{
    display: none !important;
}

/* ===== NEWS ===== */
.result-type__news__text--container, .result__social__item
{
    border: 1px solid #474A4E !important;
}


.dropdown__item:hover
{
    color:#94969A !important;
}
.dropdown__item.dropdown__item__selected
{
    color:#3E72BF !important;
}

.result-type__item__title
{
    color: #3E72BF !important;
    text-decoration: none !important;
}
.result-type__item__title:hover
{
    color: #51A4F7 !important;
}

/*
.result-type__news__text--container a:visited
{
    color: #805555 !important;
}
.result-type__item__title:visited:hover
{
    color: #A37474 !important;
}


.wrapper-list .list__item.selected, .wrapper-list .list__item__label
{
    background: #474A4E !important;
}
*/

/* ----- Menu Gauche Icones Categories ----- */

.results-column--web .result_column__title,
.sidebar__item.sidebar__item--web:hover .sidebar__item__label, .sidebar__item--web:hover svg
{
    color: #0FBD2B !important;
}
.results-column--news .result_column__title,
.sidebar__item.sidebar__item--news:hover .sidebar__item__label, .sidebar__item--news:hover svg
{
    color: #FF7800 !important;
}
.results-column--social .result_column__title,
.sidebar__item.sidebar__item--social:hover .sidebar__item__label, .sidebar__item--social:hover svg
{
    color: #1E98F5 !important;
}
.results-column--images .result_column__title,
.sidebar__item.sidebar__item--images:hover .sidebar__item__label, .sidebar__item--images:hover svg
{
    color: #9B5AFF !important;
}
.results-column--videos .result_column__title,
.sidebar__item.sidebar__item--videos:hover .sidebar__item__label, .sidebar__item--videos:hover svg
{
    color: #F64145 !important;
}
.results-column--shopping .result_column__title,
.sidebar__item.sidebar__item--shopping:hover .sidebar__item__label, .sidebar__item--shopping:hover svg
{
    color: #FAB900 !important;
}

.sidebar__item--web:hover svg, .sidebar__item--social:hover svg, .sidebar__item--news:hover svg,
.sidebar__item--images:hover svg, .sidebar__item--videos:hover svg, .sidebar__item--shopping:hover svg
{
    animation: pop 0.2s;
}

/* ----- Menu Droite Liens/Préférences ----- */

.appmenu__content__block__title
{
    color: #FAB900 !important;
}
.appmenu__content__block__links__element__link
{
    color: #78753D !important;
}
/* ----- Préférences ----- */
.setting__parent--label
{
    color: #78753D !important;
}


/* ====================== ANIMATION ========================= */
/* ========================================================== */

@keyframes pulse
{
  0%  { transform: scale(0.7, 0.7); }
  50% { transform: scale(1, 1); }
  100% { transform: scale(0.7, 0.7); }
}
@keyframes pop
{
  0%  { transform: scale(1, 1); }
  50% { transform: scale(1.2, 1.2); }
  100% { transform: scale(1, 1); }
}

/* ========================= FIN ============================ */
/* ========================================================== */

/* ========================= FIN ============================ */
/* ========================================================== */
}
