css:
@-moz-document domain(www.kaffee-netz.de) {
/*** Allgemein ***/
  body {
    color: #291B12 !important;
  }
  #content .pageContent,
  #content .discussionListItem  { /* Seitenhintergrund */
    background-color: #F7F6F2 !important;
  }
  #content .primaryContent, /* Seitenhintergrund, Schriftfarbe */
  #content .uix_message { /* Hintergrund des Beitrags, Schriftfarbe */
    background-color: #F7F6F2 !important;
    color: #291B12 !important;
  }
/*** Beiträge ***/
  #content .discussionList .sectionHeaders, /* Titelleiste der Beitragsliste */
  #content .messageDetails { /* Balken über dem Beitrag Hellbraun */
    background-color: #794D33 !important;
  }
  #content .messageDetails .authorEnd, /* Username in dem braunen Balken über dem Beitrag weg */
  #content .uix_message .messageUserInfo .messageUserBlock .avatar, /* Avatar ausblenden */
  #content .similarThreads  { /* Ähnliche Themen ausblenden */
    display: none !important;
  }
  #content .uix_message .messageUserInfo .messageUserBlock div.avatarHolder { /* Platz sparen */
    padding: 0 !important;
  }
  #content .uix_message .signature { /*Signatur nur mit Linie oben, + Abstand */
    border-top: 1px solid #794D33 !important;
    border-right: none !important;
    border-bottom: none !important;
    border-left: none !important;
    margin-top: 2px !important;
  }
/*** Listen ***/
  #content .discussionListItem .posterAvatar, /* Kein Avatar */
  #content .discussionListItem .unreadIcon { /* linkes Chevron weg */
    display: none !important;
  }
  /* Platz sparen in jeder Zeile */
  #content .discussionListItem .titleText,
  #content .discussionListItem .lastPostInfo  {
    padding: 5px !important;
  }
  #content .discussionListItem .stats .major {
    margin-top: 0 !important;
  }
  #content .discussionListItem .stats .minor {
    margin-bottom: 0 !important;
  }
  #content .discussionListItem { /* Linienfarbe zwischen Listitems */
    border-bottom-color: #794D33 !important;
  }
}
