css:
@-moz-document url-prefix("http://depreschan.ovh/"), url-prefix("https://depreschan.ovh/") {
html, body {      
  background: #ffec9b url("https://image.prntscr.com/image/102d808eec5240469b412a12ccf34154.png") repeat-x fixed 0% 0% / cover;
  /* background-repeat: space; */
  /* background-repeat-x: inherit; */
  color: #fbfbfb;
  font-family: Tahoma, "Geneva CY", sans-serif;
  font-size: 11pt;
}

a:hover,.intro a.post_no:hover
{
    color:#e52214;    
}

a, a:visited
{
 color:#81A2BE;   
}

.subject
{
 background: inherit !important;
    color: #b294bb !important;
    font-weight: bold !important;   
}

.body a
{
 color: #5F89AC !important;
    text-decoration: underline !important;   
}

header div.subtitle, h1 {
    color: #b294bb;
    text-align: center;
}

span.spoiler {
  background: #2C2C2C;
  color: rgba(255, 255, 255, 0.5);
  padding: 0 1px;
}

div.ban h2
{
 color:white;
    background: #443f3f;
}

div.ban p
{
background:#5e5757;
}

div.ban
{
 background:#5e5757;   
}

.quote
{
 color: #4fba21 !important;  
}

span.name
{
 color:#00b3b3 !important;   
}

form table tr th {
  background: rgba(164, 164, 164, 0.1);
   box-shadow: 0 0 5px rgba(0,0,0,0.5);
}

.dropzone
{
 background-color: rgba(69, 69, 69, 0.3);
}

div .post.reply
{
 background-color:rgb(42, 42, 42);
        box-shadow: 0 0 5px #000;
}

div .post.reply.highlighted
{
  background-color:rgb(42, 42, 42);  
}

div .post.op
{
   background-color:rgba(255, 0, 0, 0);
}

#options_div, #alert_div
{
  background-color: rgb(78, 78, 78);  
}
}
