div.bannerPanel{
    display:none;
}
body{
    background:none;
    background-color:#F8F8FF;
}
img{
    opacity: 0.2;
}
img:hover{
    opacity: 1;
}
.oldDesign .htbox .htbox-header {
    background-color: #4272a1 !important;
