css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("9gag.com") {
  #container, .CS3, .post-afterbar-a.in-post-top, .notification-menu, #jsid-upload-title, .section-header, .section-nav
  {
    background-color:#2F3136!important;
    color:#FFF!important;
  }
  
  h1, h2, h3, h4 
  {
    color:#FFF!important;
  }
  
  p, .username, body  
  {
   color:#C0C1C2!important; 
  }
  
  a, .headbar-search
  {
  color:#FFF!important;
  background-color: #2F3136!important;
  }
  
  a:hover 
  {
   background-color:#282B30!important;
  }
  
  .btn
  {
   background:none!important;
   background: linear-gradient(to bottom, #87A5FF 0%,#7A94E8 22%,#6B80CA 30%,#6B80CA 100%)!important;
   border:0!important;
   border-radius:3px!important;
  }
  
    input[type="text"], input[type="password"], input[type="email"]  
  {
    background:#2A2D31!important;
    border:1px #1D1F22 solid!important;
    border-radius:2px!important;
    color:#FFF!important;
  }
  
  section.modal {
    background-color: rgba(62, 65, 70, 0.99) !important;
}
  .naughty-box {
    display:none!important;
  }
}
