  /*Toggle*/
  .Toggle-inner::before{background-color:#657586}
  .Toggle.is-checked .Toggle-inner::before{background-color:#069}

  .ads,.TagkeyArticleBox,.HeaderContainer-ads,.HeaderContainer-logo{display:none}
	
	/*image*/
	.columnChange{background-color:transparent!important}
	.columnChange li{background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAEYAAAAXCAMAAACBIsnkAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAAlQTFRFd4iZZXWGAAAATBby7QAAAAN0Uk5T//8A18oNQQAAAFVJREFUeNpiYAQCJkYwyYTBhnCQALI4MskAYUIALjYywGfMoHENE1UAtYwZjamhElNUMgYEoCQTBhvCQQLI4hhqkM3Exka3F5cxg8Y1ozE1RGIKIMAADFUIverVjNwAAAAASUVORK5CYII=")}
	.columnChange.close{background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABMAAAASCAYAAAC5DOVpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAGVJREFUeNpiaJ+x8sz///8ZqIGZGKgIRrphVd1z/lPNsH///jFUdM763zJ12WKqefPLly8xxLqSqDAj1pVUjQAWopzPxMTQVprCSLE3eXh4lhBjEF6XEesaolxGqkGjeZM8ABBgANN/Vz20se/tAAAAAElFTkSuQmCC")}
	.columnChange.open{background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABMAAAASCAYAAAC5DOVpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAG1JREFUeNpi6J+z8sz///8ZqIGZGKgIRg3DBNXds//jk2chxpCOaUsXf/j8NYaQOhZiXPP333+iXM5CqWvoHwEVWdGxHeVpjMxMjNRzWWtpKqMAL/cSokwjJW9Wdc36T7W8CXLlaN6krmEAAQYAaMyKmyyv3ZsAAAAASUVORK5CYII=")}
}

@-moz-document regexp("http(s)?://www.nicovideo.jp/watch/(?:sm|nm|so)?[0-9]+([?].*)?"), regexp("http(s)?://www.nicovideo.jp/watch/[0-9]+/.*") {
/*Html5 Player*/

  .IchibaContainer,.WatchAppContainer-switchToFlash,.MarqueeContainer,.BottomContainer-side,.VideoMenuContainer-area .UadButtonBalloon,.MiniBannerAdContainer,.BottomContainer-card:nth-child(2),.NicoSpotAdContainer,.BottomSideContainer,.BottomContainer-adContainer,.LikeTeaching,.Balloon-arrowWrap,.WakutkoolFooterContainer,.WakutkoolHeaderContainer,.WakutkoolNoticeContainer,.Balloon,.EasyCommentContainer-caption{display:none}
  
  .HeaderContainer,.BottomContainer,.HeaderContainer{background:0 0}
  
  .VideoTitle,.Card-title{color:#abc}
  
  .VideoDescriptionExpander .VideoDescriptionExpander-switchExpand{background:linear-gradient(90deg,transparent 0,#111216 16%)}
  
  .Card{background:0}
  .InView.PlaylistContainer{background:#000}

  .ContentTree-parentSummary .Link{background:#000}
  .VideoMenuContainer::before{border:0}
  
  /*Player size*/
  .MainContainer-player{width:calc(100% - 426px)!important}
  .MainContainer{width:auto!important}
  .MainContainer-commentPanel,.MainContainer-playerPanel{width:426px!important}
  body.is-middle .WatchAppContainer-main{width:1280px!important}
  body.is-middle .VideoContainer{height:480px;width:auto!important}
  body.is-middle .BottomContainer{max-width:1280px;width:auto}
  body.is-large:not(.is-fullscreen) .WatchAppContainer-main,body.is-autoResize:not(.is-fullscreen) .WatchAppContainer-main{width:100%!important}
  body.is-large:not(.is-fullscreen) .VideoContainer,body.is-autoResize:not(.is-fullscreen) .VideoContainer{height:calc(40vw)!important;max-height:calc(100vh - 185px)!important;min-height:360px!important;width:auto!important}
  body.is-large:not(.is-fullscreen) .BottomContainer,body.is-autoResize:not(.is-fullscreen) .BottomContainer{max-width:none;width:auto;padding:0 16px}
	body.is-autoResize:not(.is-fullscreen) .MainContainer-player{width:calc(100% - 12px)!important}
	body.is-autoResize:not(.is-fullscreen) .MainContainer-commentPanel > div,body.is-autoResize:not(.is-fullscreen) .MainContainer-playerPanel > div{opacity:0}
	body.is-autoResize:not(.is-fullscreen) .MainContainer-commentPanel,body.is-autoResize:not(.is-fullscreen) .MainContainer-playerPanel{float:right;width:12px!important;background-color:#345;overflow:hidden;z-index:99;transition:width .7s ease }
	body.is-autoResize:not(.is-fullscreen) .MainContainer-commentPanel:hover > div,body.is-autoResize:not(.is-fullscreen) .MainContainer-playerPanel:hover > div{opacity:1}
	body.is-autoResize:not(.is-fullscreen) .MainContainer-commentPanel:hover,body.is-autoResize:not(.is-fullscreen) .MainContainer-playerPanel:hover{width:426px!important;background-color:#111216;opacity:.9}
  
  /*Player*/
  .CommentPostContainer-commentInput,.CommentPostContainer-commandInput{background-color:#000;border:0}
  .CommentInput-textarea,.CommentCommandInput{color:#789}
  .SeekBar,.VolumeBar{background:#333}
  .SeekBar-played,.VolumeBar-progress,.CommentPostButton.ActionButton{background:#069}
  .CommentPostButton.ActionButton:hover{background:#07a}
  .SeekBar-buffered, .SeekBar-hover{background:#667}
  .ControllerButton svg{fill:#667}
  .ControllerButton:hover svg,.ControllerButton.PlayerRepeatOffButton svg,.VideoMenuContainer-area .VideoMenuButton:hover:not([disabled]) svg{fill:#069}
  .CommentCommandPaletteNode[data-type=layout], .CommentCommandPaletteNode[data-type=size]{background-color:#000;border-color:#667}
  .CommentCommandPaletteContainer{background-color:rgba(0,20,40,.7)}
  .VideoContextMenuContainer-ngMenu{border-color:#222}
  .CommentShutter.CommentShutter--frost{background-color:rgba(12,12,12,.8)}
  .SeekBar,.VolumeBar{background:0}
  .VolumeBar:hover .ProgressBar{border-radius:3px;overflow:hidden}
  .PlayerOptionMenuGroup-groupName{color:#abc;border-color:#222}
  .PlayerOptionMenuItem-title{color:#abc}
  .PlayerOptionRadioGroup .RadioItem input:checked + .RadioItem-label{background-color:#069}
  .PlayerOptionRadioGroup .PlayerOptionRadioGroup-inner{background-color:#272727}
  .PlayerOptionDropdown-toggle{background-color:#069}
  .PlayerOptionDropdownItem-inner{color:#789}
  .PlayerOptionDropdownItem.is-selected .PlayerOptionDropdownItem-inner{background-color:#069}
  .PlayerOptionDropdownItem-inner:hover{background-color:#272727}
  .PlayerOptionDropdown-menu{border-color:#789}
  .VideoContainer .VideoAdContainer{display:none}
  #CommentRenderer{width:100%!important}
  #CommentRenderer canvas{width:100%!important;height:auto!important}
  .ControllerBoxCommentAreaContainer,.EasyCommentContainer{background-color:#111216;border:0}
  .LikeActionButton,.EasyCommentButton{border-color:#657586;background-color:#111216;color:#789;border-radius:4px}
  .LikeActionButton:hover,.EasyCommentButton:hover{background-color:#272727}
  .EasyCommentButton-iconWrapper{display:none!important}
  .CommentShutter:empty{background-color:#0007}
  .ControllerBoxCommentAreaContainer.is-noLogin:hover .ControllerBoxCommentAreaContainer-commentShutter{background-color:#000a}
  .CommentShutter .Link{background-color:#069}
	.EasyCommentContainer{height:24px}
.EasyCommentContainer-prevButton,.EasyCommentContainer-nextButton{border:none}
.EasyCommentContainer-scrollButton{background-color:#069}
.EasyCommentContainer-scrollButton svg{fill:#111216}
.EasyCommentContainer-scrollButton:disabled svg{fill:#789}


  /*Frame display order*/
  .WatchAppContainer-main{display:flex!important;flex-direction:column}
  .MainContainer{order:1!important}
  .HeaderContainer{order:2!important;padding-top:0!important;padding:0 16px}
  .BottomContainer{order:3!important;padding-top:0!important;min-height:0}
  .HeaderContainer{display:flex!important;flex-direction:column}
  .TagContainer{order:-1!important}
  .VideoDescriptionContainer{order:5!important}
  
  /*Tag*/
  .TagItem{background:#111216;border-color:#657586}
  .TagItem:hover{border-color:#069}
  .TagItem:hover .TagItem-name{color:#069}
  .TagItem-name{color:#657586}
  .TagContainer.is-editing{background:#111216;border:2px solid #069}
  .TagContainer-editButton{background:#657586}
  .TagEditForm-submitButton{background:#069}
  .NicoDicIcon.is-unavailable{background:#657586}
  .NicoDicIcon.is-unavailable:hover{background:#069}
  
  /*Comment*/
  .MainContainer-commentPanel,.MainContainer-playerPanel{background:#111216}
  .CommentPanelMenuContainer{border:0}
  .CommentPanelContainer,.PlayerPanelContainer{width:auto;bottom:0}
  .CommentPanelContainer,.CommentPanelContainer .react-grid-HeaderCell,.CommentPanelDataGrid .CommentPanelDataGrid-headerCell,.PlayerPanelContainer,.CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell{background:#111216;border-color:#272727}
  .CommentPanelContainer .react-grid-Cell,.CommentPanelDataGrid [class*="___table-cell___"],.PlayerPanelContainer-tabItem,.CommentPanelDataGrid .CommentPanelDataGrid-TableCell{border-color:#272727}
  .CommentPanelContainer .CommentPanel,.CommentPanelContainer .CommentPanelDataGrid{color:#789}
  .CommentPanelContainer .CommentPanelDataGrid [class*="___header___"],.CommentPanelContainer .CommentPanelDataGrid [class*="___header-table___"],.CommentPanelDataGrid [class*="___table___"]{width:409px!important}
  .CommentPanelDataGrid [data-name="no"]{max-width:50px!important}
  .CommentPanelContainer [data-row-nicoru-rank="1"],.CommentPanelContainer [data-row-nicoru-rank="3"]{background-color:#333}
  .CommentPanelContainer .expandToolInner{width:409px!important}
  .CommentPanelContainer .react-grid-Row:hover,div[role="row"]:hover{background:#272727!important}
  .CommentPanelContextMenuContainer{background-color:#111216;border-color:#789}
  .CommentPanelContextMenuContainer-menuItem:hover{color:#000;background-color:#789}
  .VideoMenuContainer-area,.VideoMenuContainer-areaLeft, .VideoMenuContainer-areaRight{background:0}
  .VideoMenuContainer::after,.VideoListPanelContainer .NextPlayVideoContainer{border:0}
  .VideoMenuContainer-area .VideoMenuButton svg,.VideoMenuContainer-areaLeft .VideoMenuContainer-button,.CommentPanelMenuContainer svg{fill:#657586}
  .VideoMenuContainer-areaLeft .LoginRequirer:not(.is-noLogin) .VideoMenuContainer-button:hover:not([disabled]){fill:#069}
  .VideoMenuContainer .UadButtonBalloon{background:#111216;border-color:#069}
  .VideoMenuContainer .UadButtonBalloon::after,.VideoMenuContainer .UadButtonBalloon::before{border-right-color:#069}
  .DropDownMenu{background:#657586;border:0}
  .PlayerPanelContainer-tabItem.current{border-color:#069}
  .WatchRecommendation-heading,.NextPlayVideoContainer-controls,.NextPlayVideoContainer-continuous,.VideoMediaObject-postedDate,.VideoMediaObject-meta{color:#789}

  .NGSettingPanelContainer{background:#111216}
  .NGSettingPanelContainer-removeButton{background:#069}
  .NGSettingPanelContainer-removeButton[disabled],
  .NGSettingPanelContainer-openViewerNGRegisterButton{background:#272727}
  .NGSettingPanelContainer-num,.NGSettingPanelContainer-enableCheckbox{color:#789}
  .Checkbox .Checkbox-check::before{background:#272727;border-color:#456}
  .NGSettingPanelContainer-contentMain{background:#111216}
  .NGSettingPanelContainer-enableCheckbox .Checkbox-check::after,
  .ViewerNGList .Checkbox-check::after{border-color:#07a}
  .ViewerNGList .ViewerNGListDataGrid-HeaderCell{background:#111216;border-color:#272727}
  .ViewerNGList .ViewerNGListDataGrid-TableCell{border-color:#272727}

  .ViewerNGRegister{background:#272727}
  .ViewerNGRegister-header{border-color:#111216}
  .ViewerNGRegister-input{color:#789;background:#111216;border-color:#657586}
  .ViewerNGRegister-radioItem label.RadioItem-label::before{background-color:#069}
  .ViewerNGRegister-radioItem label.RadioItem-label::after{border-color:#657586}
  .ViewerNGRegister-registedNum{color:#069}
  .ViewerNGRegister-cancelButton,.ViewerNGRegister-submitButton:disabled{background:#456;border-color:#456}
  .ViewerNGRegister-submitButton{background:#333;color:#07a;border-color:#07a}
  .ViewerNGRegister-submitButton:hover{background:#07a}

  /*Playlist*/
  .PlaylistContainer-cardButton-inactive{background:#657586}
  .PlaylistPager-arrow,.PlaylistContainer-playlistShuffleButton{background:#069}
  .PlaylistContainer-playlistShuffleButton:hover,.PlaylistContainer-cardButton-active{background:#07a}
  .PlaylistItemContent,.PlaylistItemList-item.currentItem{background:#111216}
  .PlaylistPager-arrow{fill:#111216}
  .PlaylistPager-arrow:hover{fill:#07a}
  .PlaylistPager-arrow.is-disabled{fill:#069}
  .PlaylistCardItem:nth-child(4n+1),.PlaylistCardItem:nth-child(4n){margin-right:8px}
  .PlaylistCardItem.is-playing .PlaylistItemContent{background:#abc}
  .PlaylistCardItem.is-playing .PlaylistItemContent-title{color:#000}
  .PlaylistContainer-inner{background:#000}
  .PlaylistItem .RouterLink{color:#abc}

  
  /*info*/  
  .VideoMetaContainer > *, .VideoMetaOverflowMenuContainer{border-color:#272727;box-shadow:none}
  .BottomContainer-main,.BottomContainer-cardMain{padding:0}
  .SearchBox,.SearchBox-input,.SearchBox-optionMenu{background:#657586;border:0}
  .TagSuggestionInput-input{background:#657586;box-shadow:inset 0 0 3px #000;color:#000}
  .SearchBox-optionMenuItem:hover{background:#556576}
  .VideoDescriptionContainer{width:auto}
  .VideoDescriptionSeriesContainer{border-color:#272727;margin:0}
  .VideoMetaOverflowMenuContainer-menu{background-color:#272727;border:0;box-shadow:0 1px 3px #000}
  .VideoMetaOverflowMenuContainer-menuBlock:not(:first-of-type){border-color:#111216}
  .VideoMetaOverflowMenuContainer .VideoMetaOverflowMenuOpenButton svg{fill:#657586}
  .VideoMetaOverflowMenuContainer-menu .CloseButton svg{fill:#657586}
  .FollowButton-label{color:#069}
  .FollowButton-button{border-color:#07a}
  .FollowButton svg{fill:#07a}
	.ContentTree-content + .ContentTree-content{border-color:#272727}
}

@-moz-document regexp("http(s)?://www.nicovideo.jp/search/.*") {

/*Search Results*/
  
  p,.relatedInfo .exTxt,.video .itemData .count .value a,.video .itemData .count .value,.video .itemDescription,.video .itemTime{color:#789}
  .video .itemData .count,.tagCaption .tagListBox .contentHeader .edit a,.videoRcolumn .itemDescription{color:#8a93a1}
  .inner{width:auto}

  /*Header*/
  .siteHeader{display:none}
  
  /*searchbar*/
  .tagCaption{background-color:#111216;margin-top:34px;border:0}
  .tagCaption .inner{display:flex;flex-direction:row}
  .tagCaption .tagListBox{background:0}

  .formSearch{padding:4px 16px;background:0;border:1px solid #789;box-shadow:none;margin:16px 20px 16px 32px}
  .formSearch .searchFormB{background:0;border:0;height:auto;width:auto;min-width:410px}
  .formSearch .searchFormB .sbubmit input{background:#657586;box-shadow:inset 0 0 4px #000;color:#000;padding:4px;height:26px;width:calc(100% - 10px)}
  .clear-button-inner-search{background:0;color:#000;right:6px;top:6px}
  .formSearch .searchFormB .searchSubmit{display:none}
  
  .searchOption{background-color:#111216;border-color:#000}
  .searchOption .optionList ul li.active .search-option-sort-item-label, .searchOption .optionList ul li.active .search-option-date-range-item-label{background-color:#789;color:#000}
  
  /*mainlist*/
  .nicoadVideoItem{display:none}
  .container .inner{display:flex;flex-direction:row;width:auto;padding:0 16px}
  .column700-300 .main{width:auto;min-width:auto;max-width:none;flex-grow:1}
  .column700-300 .main .contentBody.uad.video > ul.list{display:flex;flex-wrap:wrap}
  .video .item{flex-grow:1}
  .video .item:nth-child(4n){margin-right:20px}
  .videoList02 .item:nth-child(2n){margin-right:28px}
  .videoList01 .item{border-color:#272727}
  .videoList01 .itemContent .itemComment{color:#456;background-color:#070707;box-shadow:inset 1px 1px 4px #000;border:0;line-height:24px;height:auto}
  .videoList02 .itemContent .itemComment{color:#456;background-color:#070707;box-shadow:inset 1px 1px 4px #000;border:0;height:auto}
  
  /*subbar*/
  .sub{background:#141414}
  .container.columns .column{width:280px;padding:0;margin-left:20px}
  .list .contentBody{padding:12px 12px 8px 12px}
  .list .contentHeader{background-color:#091622}
  .list .contentHeader .title{color:#567}
  .list .contentHeader .title::before{background-color:#123}
  div[data-follow-container=""]{display:none!important}
  .trendtag.trendtagFooter{position:static;width:auto}
  .trendtag .item{border-color:#123}
  .trendtag .item:hover{background:0}
  .trendtag .tagWrapBox .tagWrap .itemTitle{background:0;border-color:#789}
  .trendtag .tagWrapBox .tagWrap .itemTitle .tagName{border-color:#789}
  .trendtag .tagWrapBox .tagWrap:hover .itemTitle .tagName{border-color:#111216}
  .trendtag .tagWrapBox .tagWrap a{color:#789}
  .trendtag .tagWrapBox .tagWrap a:hover{color:#111216;background-color:#789;border-color:#111216}
  .trendtag .discriptionBox{background-color:#789;box-shadow:1px 2px 4px #000}
  .trendtag .discriptionBox::after{border-left-color:#789}
  .trendtag .discriptionBox .itemDescription,.trendtag .discriptionBox .itemDescription a{color:#000}
  .trendtag .videoComment{color:#456;background-color:#070707;border:0;box-shadow:inset 1px 1px 4px #000}
  .trendtag .videoComment::after{border-top-color:#070707}

  /*buttons*/
  .button.default{border:0;background-color:#069;color:#ccc}
  .button.default:hover{border:0;background-color:#07a}
  
  .switchingBtn,.tagCaption .tag{background:0;border-color:#789;color:#789}
  .switchingBtn.expand, .switchingBtn:hover,.switchingBtn.active,.tagCaption .tag:hover{background-color:#789;color:#000}
}

@-moz-document regexp("http(s)?://www.nicovideo.jp/tag/.*") {
/*Tag Search Results*/
  
  p,.relatedInfo .exTxt,.video .itemData .count .value a,.video .itemData .count .value,.video .itemDescription,.video .itemTime{color:#789}
  .video .itemData .count,.tagCaption .tagListBox .contentHeader .edit a,.videoRcolumn .itemDescription{color:#8a93a1}
  .inner{width:auto}
  
  /*Header & searchbar*/
  .header{background:0}
  .header .inner{padding:0;width:280px;position:absolute;right:16px;top:48px;z-index:20}
  .siteHeader .notice,.siteHeader .pageLogo{display:none}
  
  .tagCaption{background-color:#111216;border:0}
  .tagCaption .inner{padding:0 15px 0 18px}
  .tagCaption .tagListBox{background:0}
  .tagCaption .itemDescription p .more{color:#069}
  .tagCaption .contentsData .share{display:none}
  
  .suggestBox{border:0;background:0;min-width:250px}
  .suggestBoxInner{background-color:#789;border:1px solid #789}
