css:
@-moz-document domain("www.animestreams.net") {
.main-header {
    background-color: #444444;
    color: white;
    z-index: 1;
}
.main-container-wrap {
    background-color: #212121;
    color: white;
    z-index: 0;
}
#s {
    background-color: #212121;
    color: white;
    border-Color: #444444;
}
.single_post {
    background-color: #212121;
    color: white;
}
li {
    background-color: #212121;
    color: white;
    border-bottom-width: 0px;
    border-bottom-style: hidden;
    border-color: #212121;
}
.title.single-title {
    background-color: #212121;
    color: white;
}
.le-server {
    background-color: #212121;
    color: white;
    border-width: 0px;
}
.widget-title {
    display: block;
    color: white;
    background: #444444;
    background-color: #444444;
}
.textwidget {
    visibility: hidden;
}
h2 {
    background-color: #212121;
    color: white;
}
a {
    color: white;
}
.categories {
    background-color: #444444;
    color: white;
}
.latestPost.excerpt {
    background-color: #212121;
    color: white;
    border: 2px;
    border-color: white;
}
.idE5ssksu5Nxz2HoXGg,
#M162790Composite73228,
#M162790ScriptRootC52296,
.a3tmpIn2 {
    display: none !important;
    min-width: 0 !important;
    max-width: 0 !important;
    width: 0 !important;
    visibility: hidden !important;
    height: 0 !important;
    opacity: 0!important;
}
}
