@-moz-document domain("uloz.to"), domain("ulozto.cz"), domain("ulozto.sk"), domain("ulozto.net"), domain("zachowajto.pl") {
/* Ulo≈æ.to! Fix by R3gi, Czech Republic
Version: 2018.08.15.17.37 */

/* Top padding */
.page-branding .pg-wrapper {
	padding-top: 0;
}

.pg-header,
.page-branding .pg-wrapper .pg-wrapper-bg>.l-header {
	border-top: none !important;
}

.page-branding .pg-wrapper .pg-main,
.page-branding .pg-wrapper .pg-wrapper-bg>.pg-header,
.page-branding .pg-wrapper .pg-wrapper-bg>.l-header {
	border-left: none !important;
    border-right: none !important;
}

/* Hidden ads */
#adBranding,
.ad-sky-container,
.banner,
.adFull {
	display: none;
}

/* Page width */
.page-branding .pg-header, .page-branding .pg-main,
.page-branding .agreement, .page-branding .pg-footer,
.page-branding .pg-wrapper .pg-wrapper-bg>.pg-header,
.page-branding .pg-wrapper .pg-wrapper-bg {
	width: 100%;
}
}
