css:
@-moz-document domain("lichess.org")
{  
  .cg-board square.selected
  {
    background-color: rgba(15,0,125,0.5) !important;
  }

  .cg-board square.move-dest
  {
    background: radial-gradient(rgba(15,0,125,0.3) 17%,#208530 0,rgba(0,0,0,0.3) 0,rgba(0,0,0,0) 0) !important;
  }

body.dark
  {
    background-image: url("http://i.imgur.com/1jlCAy7.jpg")  !important;
    color: #EEE;
  }
  
  .purple .is2d .cg-board,
#top div.color_demo.purple
  {
        background-image: url("http://i.imgur.com/zcid2Mq.png")  !important;
  }

  .is2d .cg-board
  {
    image-rendering: auto;
  }

  .lichess_board
  {
    padding: 50px;
  background-image: url("https://i.imgur.com/3UNCwks.png")  !important;
  background-size: 100% 100%;
  }
  
  .underboard
  {
    margin-top: 50px !important;
  }

  .ranks coord
  {
    margin-left: 3px !important;
  }

  .files coord
  {
    margin-top: 0px !important;
  }

  .cg-board square.last-move 
  {
    background-color: rgba(15,0,125,0.5) !important;
  }
}
