css:
@-moz-document domain("open.spotify.com") {
a.cover-art.shadow.light.actionable {
    border-radius: 50%;
    border-style: none;
    border-color: #e63ce6;
}
div.react-contextmenu-wrapper {
    border-radius: 50%;
}
div.hoverable {
    border-radius: 50%;
}
div.media-object {
    border-style: none;
}
.cover-art.light:after {
    display: none;
}
div.cover-art.shadow.light.actionable {
    border-radius: 50%;
}
div.cover-art.shadow.now-playing__cover-art {
    border-radius: 50%;
}
div.cover-art-image.cover-art-image-loaded {
    border-radius: 50%;
}
}
