@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("google.com") {

/* MESSAGE ROWS */
/* ============ */

/* Unread Message Rows */
/* ------------------- */
/* Fixed Highlight for Unread*/
tr[class^="xweT7d QhHSYc"] {background-color: #276BA5 !important;}

/* Hover Highlight for Unread */
table.N2mZ7b tr.QhHSYc:hover, table.tlc tr.ur:hover { background-color: #5F95D7 !important;}


/* Read Message rows */
/* ----------------- */
/* Fixed Highlight for Read */
.AnqB9d {background-color:#262626 !important;}

/* Hover Highlight for Read */
table.N2mZ7b tr.AnqB9d:hover, table.tlc tr.rr:hover { background-color: #5B5B5C !important;}


/* Selected Message rows */
/* --------------------- */
/* Fixed Highlight for Selected */
.rfza3e {background-color : #4E9080 !important;
color : #000000 !important;}

/* Hover Highlight for Selected */
table.N2mZ7b tr.rfza3e:hover, table.tlc tr.ur:hover { background-color: #1EC1B7 !important;}


/* LEFT NAVIGATION PANEL */
/* ===================== */

/* Hovering Mail Categories (Inbox, Starred, Chats, etc.) */
.ACpQre { margin-right:-2px !important;}
.ACpQre:hover { background-color: #526998 !important;} 

/* Hovering Label Names */
.WnjAod:hover { background-color: #66BFD9 !important;}
.pvSW6e { padding: 0 !important;}
.BFvfre { padding:2px !important; width: 18px !important;}
.WnjAod .yyT6sf { margin: 2px 0px -2px 5px !important;}

/* Hovering Chat List */
.oOukxe:hover { background-color: #66BFD9 !important;}

/* Hovering Quicklinks List */
div[class^="ooCKse"]:hover  a { background-color: #66BFD9 !important; }

}
