css:
@-moz-document domain("pandora.com") {
div[class*='Nav--scrolled'],
.SearchResultsList::before,
.overlay-circle,
.Modal__container,
.Nav--upgradeTheme,
.Nav--upgradeTheme.Nav--scrolled,
.Footer--dark,
.Nav--lightTheme.Nav--scrolled,
.DropdownBox,
.AutocompleteMenu,
.Search--active,
.Search--active.Search--rightRail,
.Nav--darkTheme.Nav--scrolled,
.NowPlaying__belowFold,
.Tuner,
.region-topBar,
.BlurredBackground,
.EmptySearch__content::before,
.BlurredBackground__svg__image,
.AppBg::after,
.region-main__content {
    background-color: #1C1C1C !important;
}

.SourceCard__menu, .SourceCard__subMenu,
.GlobalSearchInput__input:focus,
.GlobalSearchInput--overlayActive .GlobalSearchInput__input,
.GlobalSearchInput--overlayActive .GlobalSearchInput__input:focus{
    background-color: #333 !important;
}

.Duration--Dark .Duration__Spacer__Bar,
.VolumeDurationControl--Dark .VolumeDurationControl__VolumeSlider__Handle,
.VolumeDurationControl--Dark .VolumeDurationControl__VolumeSlider__Trail{
    background-color: #777 !important;
}

.TunerProgress__Progress {
    background: hsla(0, 0%, 60%, .6) !important;
}

.GlobalSearchInput__iconSearch,
.ButtonRow__button,
.NavSecondary--lightTheme--rightRail .NavSecondary__logo--icon,
.SearchField--clear .SearchField__clear,
.SearchField--placeholder .icon-search,
.SearchField--placeholder .SearchField__placeholder,
.GlobalSearchInput--dark .Icon,
.NavSecondary__logo--icon,
.IconInteractive,
.SourceCard__menu__item__icon,
.SourceCard--dark .SourceCard__ellipsis .Icon,
.CollectionButton--nowPlayingTopInfoButton .icon-check, 
.CollectionButton--nowPlayingTopInfoButton .icon-plus,
.SourceCard__ellipsis .Icon,
.GlobalSearchInput__clear,
.GlobalSearchInput--activeFocused .GlobalSearchInput__clear__icon,
.GlobalSearchInput--inputFilled .GlobalSearchInput__clear__icon,
.SearchField--darkTheme .SearchField__placeholder.IconInteractive--small{
    fill: #777 !important;
}
body,
.GlobalSearchInput__input,
.overlay-header,
.FilletBio__artistDescription,
.FilletBio__header,
.Duration > span,
.Lyrics--backstage .Lyrics__lyrics__wrapper p,
.Lyrics__footer__credits,
.GridItem__caption__text,
.HeaderBackstage__text__subTitle,
.ContentSection__title,
.RowItem__rightCol__child,
.DescriptionBackstage__content, 
.RowItem__centerCol__secondText,
.RowItem__centerCol__thirdParagraph,
.RowItem__centerCol__thirdText,
.RowItem__centerCol__mainText,
.NavHorizontal__item__link--active,
.BrowseGenresCta__label,
.GridItem__metacontent__second,
.Redeem__checkbox__row--text,
.Redeem__email,
.Redeem__incorrectEmail__row,
.Redeem__input--error .Redeem__form__inputErrorMessage,
.Redeem__row,
.Upgrade__splash__link,
.Upgrade__splash__link:active,
.Upgrade__splash__link:focus,
.Upgrade__splash__link:hover,
.Upgrade__splash__link:visited,
.Upgrade__termsSection,
.UpgradeCard__title,
.UpgradeCard__name,
.Tuner__Audio__TrackDetail__title,
.Tuner__Audio__TrackDetail__labels,
.Tuner__Audio__TrackDetail__artist,
.ArtistAudioMessages,
.AudioSettings__row,
.AudioSettings__row--highQuality,
.Switch__options,
.AudioSettings__toggle--text,
div.Notifications__row,
.AccountInfo__editButton,
#twitter,
#twitterInformation,
#facebook,
#facebookInformation,
div.UserSettings__row:nth-child(2) > div:nth-child(1) > label:nth-child(1),
div.UserSettings__row:nth-child(1) > div:nth-child(1) > label:nth-child(1),
.FilletBio--lightTheme .FilletBio__artistDescription__name, 
.FilletBio--lightTheme .FilletBio__artistDescription__summary, 
.FilletBio--lightTheme .FilletBio__header, 
.FilletBio--lightTheme a,
.AccountInfo__text,
.AccountInfo__row .AccountInfo__whyPandoraQuestion__link,
.AccountInfo__sub-header,
.UserSettings__instruction,
.SideNav__heading,
.SideNav__heading--upgrade,
.UserProfile__Profile__editButton,
.UserProfile__Bookmarks__subHeader,
.Footer__topMenu__item,
.MediaListItem__tertiaryText,
h1,
.SideNav__list__link,
.SideNav__list__link__subLink,
.SideNav__list__link--active,
.SideNav__list__link--active:visited,
.UserProfile__Profile,
.UserProfile__Profile__header__name,
.SearchField__input,
.SearchListItem__text__secondLine,
.SearchListItem__text__firstLine,
.AutocompleteMenu__header__title,
.ArtistDetailSubnugget__header,
.MiniBar__content__stationName,
.MiniBar,
.Footer__copyrightText,
.SimilarArtistsSubnugget,
a,
a:focus,
a:hover,
a:visited,
.Footer__topMenu__item,
.SimilarArtistsItem__name,
.SimilarArtists--backstageStation .SimilarArtists__title,
.SimilarArtists__title,
.Marquee__wrapper__content__child,
.FilletBio__CTA__btn,
.FilletBio__artistDescription__summary,
.FilletBio__artistDescription__header,
.Lyrics--nowPlaying .Lyrics__divider__button,
.Lyrics--nowPlaying,
.nowPlayingTopInfo__current__albumName,
.nowPlayingTopInfo__current__artistName,
.Marquee__wrapper__content,
.Button--StationList--seeAll,
.Nav__primaryMenu__item__link,
.Nav__primaryMenu__item__link--active,
.Footer__copyright,
.FocusTraits__content__innerWrapper p,
.Copyright__text,
.GlobalSearchInput--overlayActive .GlobalSearchInput__input,
.EmptySearch__content__title,
.EmptySearch__content__subtitle,
.GlobalSearchInput--dark .GlobalSearchInput__input,
.SourceCard__menu__item--focus.SourceCard__menu__item, 
.SourceCard__menu__item:focus, 
.SourceCard__menu__item:hover,
.SourceCard__menu__item,
.SourceCard__subMenu__item__button,
.StationListItem__content {
    color: #777 !important;
}

.Nav__primaryMenu__item__link--active {
    border-color: #777 !important;
}
.Lyrics--nowPlaying .Lyrics__divider {
    border-color: hsla(0,0%,100%,.1) !important;
}
.ButtonT3,
.Button--coachmark--link,
.SingleToggleButton__button,
.ButtonLink--cta,
.ButtonLink--cta:focus,
.ButtonLink--cta:hover,
.ButtonLink--cta:visited,
.Redeem__redeem__button,
.UpgradeCard__signUp__link,
.twitter,
.facebook,
.RadioButton,
.overlay-button,
.ButtonLink--nav--upgrade--lightTheme,
.ButtonLink--nav--upgrade--lightTheme:focus,
.ButtonLink--nav--upgrade--lightTheme:hover,
.ButtonLink--nav--upgrade--lightTheme:visited,
.ButtonLink--nav--upgrade--darkTheme,
.ButtonLink--nav--upgrade--darkTheme:focus,
.ButtonLink--nav--upgrade--darkTheme:hover,
.ButtonLink--nav--upgrade--darkTheme:visited {
    color: #1C1C1C !important;
    background-color: #444 !important;
}
.SearchField--clear .SearchField__clear,
.SearchField--placeholder .icon-search,
.SearchField--placeholder .SearchField__placeholder,
.SearchField--darkTheme .SearchField__placeholder {
    color: hsla(0, 0%, 40%, .8) !important;
}
.Checkbox,
.Redeem__input--text,
.SearchField--inactive.SearchField--darkTheme .SearchField__input {
    color: hsla(0, 0%, 40%, .8) !important;
    background-color: hsla(0, 0%, 30%, .2) !important;
}
.MiniBar__content,
.MiniBar {
    padding: 0 !important;
    border-top: 5px solid #171717 !important;
    border-bottom: 5px solid #171717 !important;
    background-color: #171717 !important;
}
.VolumeDurationControl__VolumeIconContainer:hover,
.VolumeDurationControl,
.TunerControl {
    fill: #444 !important;
}
.VolumeDurationControl--expanded .VolumeDurationControl__VolumeSlider,
.VolumeDurationControl:hover .VolumeDurationControl__VolumeSlider {
    opacity: .3 !important;
}
.SingleToggleButton,
.SimilarArtistsItem__art,
.ImageLoader .ImageLoader__intrinsic,
.Avatar__img,
.nowPlayingTopInfo,
.Nugget,
.icon-down > use:nth-child(1) {
    filter: brightness(80%) !important;
    -webkit-filter: brightness(80%) !important;
}
body {
    background: none !important;
    background-color: #1C1C1C !important;
}
.Search {
    transition: width .3s ease-in-out, background-color .3s ease-in-out;
    background-color: #1C1C1C !important;
}
.AutocompleteMenu__fullSearch--wrapper {
    background-color: #1C1C1C !important;
    color: #444 !important;
}
.DropdownOption--enabled {
    background-color: #1C1C1C !important;
    color: #444 !important;
}
.DropdownOption--enabled:hover {
    background-color: #444 !important;
    color: #1C1C1C !important;
}
.SideNav__list__link--active,
.SideNav__list__link--active:visited {
    border-left: 2px solid #444 !important;
}
.Switch__slider {
    background-color: #444 !important;
}
.Switch__slider::before {
    background-color: #AAA !important;
}
.ShuffleButton__button__shuffleString {
    color: #999 !important;
}
.UpgradeCard,
.UpgradeCard--anon {
    background-color: #222 !important;
    color: #444 !important;
}
.Redeem__button :disabled {
    background-color: rgba(0, 0, 0, .2) !important;
    color: #444 !important;
}
.Container__splash {
    background: none !important;
    background-color: #1C1C1C !important;
}
.Container__splash__logo::before {
    content: "Minimalist Dark";
    position: relative;
    top: 8rem;
    left: 16rem;
    font-family: inherit;
    font-size: 3rem;
    color: #FFF;
}
.Container__splash__logo {
    opacity: 0.2 !important;
}
.MiniBar--rightRail,
.region-topBar--rightRail,
.region-bottomBar--rightRail,
.region-main--rightRail {
    width: 100% !important;
}
.SearchModal--rightRail {
    margin: auto !important;
}
.AppBg.AppBg--showImg {
    display: none !important;
}
.SearchField__placeholder{
    width: 36rem !important;
}
.Search--active, .Search--active.Search--rightRail, .Search{
    width: 40rem !important;
}
.SearchModal--focus{
    background-color: rgba(0,0,0,0) !important;
}
.AppBg__img{
    background: none !important;
}
.DescriptionBackstage__content__seeMore,
body::before{
    background: none !important;; background-color: #1C1C1C !important;
}
.SearchModal--navStyle.SearchModal--focus,
.SearchModal--navStyle.SearchModal{
    position: fixed !important;
    left: 50% !important;
    transform: translate(-50%) !important;
    -webkit-transform: translate(-50%) !important;
}
.icon-wrap{
    filter: grayscale(1) brightness(0.4) !important;
}
}
