css:
@-moz-document domain("www.facebook.com") {
html|*:not(.fbChatSidebarBody) {
    /*CSS transitions */
    -o-transition-property: none !important;
    -moz-transition-property: none !important;
    -ms-transition-property: none !important;
    -webkit-transition-property: none !important;
    transition-property: none !important;
    
    -o-transform: none !important;
    -moz-transform: none !important;
    -ms-transform: none !important;
    -webkit-transform: none !important;
    transform: none !important;
    
    -webkit-animation: none !important;
    -moz-animation: none !important;
    -o-animation: none !important;
    -ms-animation: none !important;
    animation: none !important;
}

html body {
    counter-reset: section;
}

#facebook ._-kb a, #facebook ._-kb h1, #facebook ._-kb h2, #facebook ._-kb h3, #facebook ._-kb h4, #facebook ._-kb h5, #facebook ._-kb h6, #facebook ._-kb p, #facebook ._-kb span, #facebook ._5s6c._5s6c,._5s6c {
	font-family: 'Noto Sans', 'Open Sans', 'Roboto Mono', sans-serif !important;
}


/* Tracked link */
[data-hovercard^="/ajax/hovercard"],
[onmouseover^="LinkshimAsyncLink.swap("] {
	color: #e64620 !important;
}

/* Avatar and name */
._5pat ._5sq7,
._54ru,
._4d3w._u77 ._xlv .img {
    width: 40px;
    height: 40px;
    box-sizing: border-box;    
    padding-left: 40px;
    background: url(http://findicons.com/files/icons/2579/iphone_icons/40/anonymous.png) 100% no-repeat;
}

.UFIReplyList .UFIActorImage {
    padding-left: 20px;
	background: url(http://findicons.com/files/icons/2579/iphone_icons/40/anonymous.png) no-repeat;
	background-size: 100%;
}

[data-testid^="blue_bar_profile_link"] img {
	width: 24px;
    height: 24px;
    box-sizing: border-box;    
    padding-left: 24px;
    background: url(http://findicons.com/files/icons/2579/iphone_icons/40/anonymous.png) no-repeat;
    background-size: 100%;
}

._55ln img {
    width: 32px;
    height: 32px;
    box-sizing: border-box;
    padding-left: 32px;
	background: url(http://findicons.com/files/icons/2579/iphone_icons/40/anonymous.png) no-repeat;
	background-size: 100%;
}

#facebook ._-kb ._5x46 .fwb a,
#facebook ._-kb [data-testid="fbfeed_story"] .fwb a,
.UFICommentActorName,
.UFIReplySocialSentenceLinkText,
[data-testid^="blue_bar_profile_link"] span,
._4arz {
	color: transparent;
    box-sizing: border-box;
    position: relative;
    display: inline-block;
    font-size: 0;
    line-height: 0; 
}

[data-testid^="blue_bar_profile_link"] {
    width: 99px;
}

#facebook ._-kb .fwb a,
.UFICommentActorName,
.fbChatOrderedList ._55lr {
	width: 80px;
}

#facebook ._-kb ._5x46 .fwb a:first-child:after,
#facebook ._-kb [data-testid="fbfeed_story"] .fwb a:first-child:after,
.UFICommentActorName:after,
[data-testid^="blue_bar_profile_link"] span:after,
.fbChatOrderedList ._55lr:after {
    margin-top: -5px;
    font-size: 13px;
	content: "@nonymous";
    position: absolute;
    left: 0px;
}

.fbChatOrderedList ._55lr {
	color: transparent;
}

.fbChatOrderedList ._55lr:after {
	line-height: 32px;
    margin-left: 50px;
}

[data-testid^="blue_bar_profile_link"] span:after {
	color: white;
}

.UFIReplySocialSentenceLinkText:after {
    font-size: 10px;
	content: "[!@#$%]";
    color: #000;
    position: absolute;
    left: 0px;
}

.sp_Fx3pEICssbP.sx_8f0934 {
	margin-left: 0px;
}

.UFIImageBlockContent span span abbr.livetimestamp {
	margin-left: 40px;
    position: absolute;
    top: 8px;
}

[data-profile-intro-card^="1"],
.home_right_column,
.clearfix.sideNavItem.stat_elem[data-type="type_user"],
.pam.uiBoxLightblue,
[aria-label^="Profile of"] {
	display: none;
}

[data-hover^="tooltip"]:after {
	content: '';
}

#timeline_react_composer_container ._ohe.lfloat {
	width: 500px;
}

/* Friend list space improve */
html ._262m ._698 {
    box-sizing: border-box;
    height: 115px;
    float: left;
}

._8o .img {
	margin-top: 1px;
    margin-left: 1px;
}

li.followListItem.fbProfileBrowserListItem._698:first-child,
li._698:first-child {
    border-width: 1px;
}

.fbProfileBrowserListContainer:after,
[aria-role^="tabpanel"]:after {
	content: " ";
    height: 1px;
    display: block;
    clear: both;
}
}
