css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace svg url(http://www.w3.org/2000/svg);

@-moz-document domain("golos.io") {
  
  body {
    color: #D3D3D3;
    background-color: #1E2F2C;
  }
  label {
    color: #999;
  }
  .PostFull__body {
    font-family: Helvetica Neue,Arial,Roboto,sans-serif;
  }
  .PostsList__post_container {
    background-color: #383838;
  }
  .PostsList__post_container .PostFull {
    background-color: #383838;
  }
  .Post {
    background-color: #383838;
  }
  
  
  .Markdown {
    font-family: Helvetica Neue,Arial,Roboto,sans-serif;
  }
    .Markdown h1, .Markdown h2, .Markdown h3, .Markdown h4, .Markdown h5, .Markdown h6, .ReplyEditor__body.rte h1, .ReplyEditor__body.rte h2, .ReplyEditor__body.rte h3, .ReplyEditor__body.rte h4, .ReplyEditor__body.rte h5,  .ReplyEditor__body.rte h6 {
    font-family: Roboto,sans-serif;
  }
  
  .Header__top {
    background-color: #1E2F2C;
    border-bottom: 1px solid #0f6580;
  }
  .Header__sub-nav {
    background-color: #1E2F2C;
    border-bottom: 1px solid #0f6580;
  }
  .header .menu, .Header .menu {
    background-color: #1E2F2C;
  }
  .button.success {
    background-color: #698CC0;
  }
  .button.success:focus, .button.success:hover {
    background-color: #546F98;
  }
  
  .HorizontalMenu .active > a {
    color: #ccc;
  }
  .HorizontalMenu li > a:hover {
    color: #7faa4a;
  }
  .Header__sub-nav li > a {
    background-color: transparent;
    color: #d3d3d3;
  }
  
  .button.hollow:focus, .button.hollow:hover, .button.hollow.secondary:focus, .button.hollow.secondary:hover {
    border-color: #789964;
    color: #789964;
  }
  .button.hollow, .button.hollow.secondary {
    border: 1px solid #76dd39;
    color: #76dd39;
  }
  
  a {
    color: #76dd39;
    outline: 0;
  }
  a:hover, a:focus {
    color: #56b41e;
  }
  
  .PostsIndex__topics {
    border-left: 1px solid #0f6580;
  }
  .PostSummary {
    background-color: #1E2F2C;
  }
  .PostSummary__body {
    color: #8d9ba4;
  }
  .PostSummary__header>h1>a {
    color: #698CC0;
  }
  .PostSummary__body a {
    color: inherit;
  }
  .PostSummary__image {
    border: 1px solid #05a7ff;
  }
  
  .Markdown code, .ReplyEditor__body.rte code {
    background-color: #272822;
  }
  code, kbd {
    color: #F8F8F2;
  }
  
  .Comment__header-user {
    color: #6a79cb;
  }
  blockquote, blockquote p {
    color: #3dcb5c;
  }
  .Topics__filter {
    background-color: #404040; 
  }
  .Topics__filter__expand {
    background-color: #606060;
  }
  .Topics__filter__expand:hover {
    background-color: #707070;
  }
  
  .Header .menu .hamburger::after {
    background: #e42f2f;
    box-shadow: 0 7px 0 #e42f2f,0 14px 0 #e42f2f;
  }
  .Header .menu .hamburger:hover::after {
    background: #de7e7e;
    box-shadow: 0 7px 0 #de7e7e,0 14px 0 #de7e7e;
  }
  
  .button {
    background-color: #698CC0;
  }
  .button:focus, .button:hover {
    background-color: #546F98;
  }
  
  .Feedback a {
    background-color: #698CC0;
    color: #fefefe;
  }
  .Feedback a:focus, .Feedback a:hover {
    background-color: #546F98;
    color: #fefefe;
  }
  
  .TagList__horizontal a {
    background: #757575;
    color: #d9d9d9;
  }
  
  .TagList__horizontal a:hover {
    background: #5a5a5a;
    color: #d9d9d9;
  }
  
  .dropdown-pane {
    box-shadow: 1px 1px 5px 0 rgba(215, 212, 212, 0.8);
    background-color: #404040;
  }
  .VerticalMenu > li > a {
    color: #2bd85e;
  }
  .VerticalMenu > li > a:hover {
    background-color: #606060;
  }
  
  .DropdownMenu > .VerticalMenu {
    background-color: #404040;
    padding: 5px 0;
  }
  .menu.vertical > li {
    padding: 0px;
  }
  .PostFull__footer .VerticalMenu > li > a {
    color: #76dd39;
  }
  .Voting .DropdownMenu ul li > a, .Voting .DropdownMenu ul li > span {
    padding: 0.3rem 0.8rem;
  }
  
  .UserWallet__balance.UserWallet__balance.zebra {
    background-color: #2C443F;
  }
  .secondary {
    color: #aaa;
  }
  .UserProfile__top-nav {
    border-bottom: 1px solid #0f6580;
    background-color: #1E2F2C;
  }
  .WalletSubMenu > li > a {
    color: #698CC0;
  }
  .WalletSubMenu > li > a.active {
    color: #A5C1EC;
  }
  .WalletSubMenu > li > a:hover {
    color: #A5C1EC;
  }
  
  .PostFull__time_author_category {
    color: #698CC0;
  }
  .PostFull__footer .FoundationDropdownMenu__label, .PostFull__footer a {
    color: #698CC0;
  }
  .PostFull__footer svg {
    fill: #698CC0 !important;
  }
  .PostFull__views {
    color: #698CC0;
  }
  .PostFull__footer .Reblog__button, .PostFull__reply, .PostFull__views {
    border-right: 1px solid #698CC0;
  }
  
  table tbody, table tfoot, table thead {
    background-color: #404040;
    color: #d3d3d3;
    border: 1px solid #999;
  }
  table tbody tr:nth-child(2n) {
    background-color: #505050;
  }
  
  [type=color],[type=date],[type=datetime-local],[type=datetime],[type=email],[type=month],[type=number],[type=password],[type=search],[type=tel],[type=text],[type=time],[type=url],[type=week],textarea {
    background: #202020;
    color: #999999;
    border-color: gray;
  }
  [type=color]:focus,[type=date]:focus,[type=datetime-local]:focus,[type=datetime]:focus,[type=email]:focus,[type=month]:focus,[type=number]:focus,[type=password]:focus,[type=search]:focus,[type=tel]:focus,[type=text]:focus,[type=time]:focus,[type=url]:focus,[type=week]:focus,textarea:focus {
    box-shadow: 0 0 5px #5173b9;
    background: #202020;
    color: #999999;
    border-color: #5f87d7;
  }
  
  .ReplyEditor__options select {
    background-color: #333;
  }
  select.Topics {
    background-color: #333;
    color: #fefefe;
    border: 1px solid #999;
  }
  
  .PostsList__post_overlay {
    background-color: hsla(190, 8.6%, 13.7%, 0.8);
  }
  
  .reveal {
    background-color: #2a2a2a;
  }
  
  .sell-color {
    color: #ff2f16 !important;
  }
  
  .Author__dropdown .Author__name {
    color: #698CC0;
    
  }
  .Author__dropdown .Author__username {
    color: #76dd39;
  }

  .Comment .highlighted {
    background: #5a5a5a;
    color: whitesmoke;
  }
}
