css:
@-moz-document domain("cstrike.online") {
/* 
	* Nombre: Dark f#C-S
	* Autor:  RusselNight
	* Version: 1.0.1
	* Ultima Actualizacion: 27/05/2017 - 19:05
*/

.body_wrapper, .above_body, .navtabs, .catfh, body {
    background: #282828;
    color: #E6E6E6;
    border: 0px;
}

.navtabs li.selected a.navtab {
    background: #202020;
    color: #E6E6E6;
}

.navtabs > ul.floatcontainer li a,.navtabs > ul.floatcontainer li a.popupctrl, .navbar_advanced_search li a {
    color: #00BC5B;
}

.navtabs > ul.floatcontainer li a:hover, .navbar_advanced_search li a:hover {
    color: #00DE6B;
}

.navtabs > ul.floatcontainer li:hover a.popupctrl {
    border-color: #7BC6AB;
    background-color: #004B32;
}

.navtabs > ul.floatcontainer li:hover a.popupctrl.active, .navtabs > ul.floatcontainer li a.popupctrl.active, .groupbit:hover .postcontrols a {
    color: #00DE6B;
    border-color: #7AD4B3;
    background-color: #006644;
}

.navbar {
    background: #202020;
}

.globalsearch .textboxcontainer {
    border-left: 1px solid #353535;
}

.globalsearch .buttoncontainer {
    border-left: 1px solid #353535;
    border-right: 1px solid #353535;
}

.cattabbed li {
    background: #009A4A;
    border: 1px solid #009A4A;
}

.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow, div div p .time, div#fHcount span, table tbody tr td, .wgo_block .blockbody, .wgo_block .blocksubhead, .wgo_block .section, .wgo_block .time, .shade, .navlinks, .blockbody, .groupbit:hover .blockfoot, .threadbit .nonsticky, .threadbit .discussionrow, .forum_info .blockbody, .thread_info .blockbody, .formcontrols .blockrow, .blockfoot, .blocksubfoot {
    background: #353535;
    color: #BABABA;
    border: 0px;
}

.above_postlist .pagination_top .pagination, .above_postlist .pagination_top .postpagestats {
    color: #BABABA;
}

dd .time {
    color: #BABABA;
}

.bbcode_container div.bbcode_quote, .bbcode_container div.bbcode_code, .bbcode_container pre.bbcode_code {
    background: #262626;
    border: 1px solid #00BC5B;
}

.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow, .postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    background: #353535;
    color: #F1F1F1;
}

.postbitlegacy .userinfo, .postbitlegacy .postdetails, .eventbit .eventdetails {
    background: #252525;
    color: #FAFAFA;
}

.threadbit .sticky {
    background: #252525;
    color: #C1C1C1;
    border: 0px;
}

.threadbit .alt, div#sfHcount span {
    background: 0;
    color: #CACACA;
}

.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead, .wgo_block, .threadlisthead, .threadbit, .forum_info .blockhead, .forum_info .blockbody, .thread_info h4, .thread_info .blockbody {
    box-shadow: -2px 2px 2px #176A3E;
}

body a, #forums a.username {
    color: #00BC5B;
}

body a:hover, #forums a.username:hover {
    color: #00DE6B;
}

.forumhead, .blockhead, .wgo_block .blockhead, .threadlisthead, .forum_info .blockhead, .thread_info h4 {
    background: #009A4A;
    border: 0px;
}

.newcontent_textcontrol {
    background: #009A4A;
    box-shadow: -2px 2px 2px #176A3E;
}

.blocksubhead, .forumbit_nopost .subforumdescription, .blocksubhead a, .toolsmenu {
    background: #2A3E38;
    color: #BABABA;
    border: 0px;
}

.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
    color: #BABABA;
}

.blocksubhead a:hover {
    color: #00DE6B;
}

.pagination span a.popupctrl, .pagination span a {
    background: #252525;
    color: #00BC5B;
}

.pagination span.selected a {
	background: #00BC5B;
    color: #252525;
    border: 1px solid #00BC5B;
}

.pagination span a:hover {
    border: 1px solid #00BC5B;
}

#usercp_nav .blockbody, #usercp_nav .blockrow {
    background: #353535;
}

#usercp_nav .blockrow * a:hover {
    background: #00BC5B;
    color: #252525;
}

#usercp_nav .blockrow .active, #usercp_nav .blockrow .active a {
    background: #00BC5B;
    color: #252525;
}

legend {
    color: #FFF;
}

.userinfo_extra .location {
    color: #00BC5B;
}

#yui-gen1 {
    color: #00BC5B;
}

.uStaff0, .uStaff1, .uStaff2, .uStaff3, .uStaff4, .uStaff5, .uStaff6, .uStaff7, .uStaff8, .uFree {
    text-shadow: 1px 1px #000000;
}

.footer {
    background: #009A4A;
    border: 0px;
    box-shadow: -2px 2px 2px #176A3E;
}

.new .forumicon {
    content: url(http://vvcap.com/img/iZb9IHzB6Mc.png);
    width: 48px;
    height: 48px;
}
}
