css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("grainesdemouettes.com") {
  @-moz-document regexp('^(?!(http?://|https?://)?(wiki\\.grainesdemouettes\\.com|grainesdemouettes\\.com/phpmyadmin).*).*$') {
    .main {
      background-color: #333;
    }

    .well{
      background-color: #444;
      border-color: black;
    }

    .landing-page .wrapper, .wrapper, body{
      background-color: #222;
      color:white;
    }

    h2, h3, .lien_couverture, .info .info-title {
      color: #ddd;
    }

    h4{
      color:white;
    }

    .jumbotron {
      background-color: #444;
    }

    .table-striped > tbody > tr:nth-of-type(2n+1) {
      background-color: #555;
    }
    
    .card {
      background: #444;
    }
    .card-content{
      color:white;
    }
    
    .form-control {
      color: #eee;
    }
    
    .modal-content{
      background-color: #333;
    }
    
    .modal-content h1{
      color : white;
    }
    
    .modal-content p{
      color : #ddd;
    }
    
    #leaderboard1 h4, #leaderboard2 h4, #leaderboard3 h4{
      color:black;
    }
    
    a {
      color: #00dcff;
    }
    
    a:hover {
      color: #64ffff;
    }
    
    .text-primary th{
      color:  #33ccff;
    }

  }
}
