css:
@-moz-document domain("forum.cxem.net") {
/*Размер аватарки*/
.ipsUserPhoto_large img, img.ipsUserPhoto_large, .ipsUserPhoto_large:after {
    width: 50px;
    height: 50px;
}

/*Размер аватарки без фото (буква в кружочке)*/
.toColorBlank_large {
    width: 50px;
    height: 50px;
    line-height: 50px;
    font-size: 40px;
}

/*Размер шрифта ника и отступы*/
.ipsType_sectionHead {
    font-size: 15px;
    line-height: 10px;
    font-weight: 400;
    display: inline-block;
}


/*Кружочки под ником*/
.ipsPip:before {
    content: '\f111';
    font-family: 'FontAwesome';
    font-size: 5px;
}

/*Размер шрифта статуса пользователя*/
.ipsResponsive_hidePhone {
    font-size: 12px;
}

/*Отступы от аватарки*/
.cAuthorPane_photo {
    margin: 0px;
    margin-top: 0px;
    margin-right: 0px;
    margin-bottom: 10px;
    margin-left: 0px;
    position: relative;
}


/*Отступы кнопок "Мультицитата Цитата Наверх"*/
p {
    display: block;
    -webkit-margin-before: 7px;
    -webkit-margin-after: 0px;
    -webkit-margin-start: 0px;
    -webkit-margin-end: 0px;
}



/*Размер главного шрифта*/
.ipsType_normal {
    font-size: 13px;
}

/*Межстрочный интервал*/
body {
    line-height: 14px;
}

/*Главное отступы*/
.ipsPad,
.ipsApp ul.ipsPad,
.ipsApp ol.ipsPad {
    /*padding: 0px;*/
    padding-top: 15px;
    padding-right: 10px;
    padding-bottom: 15px;
    padding-left: 10px;
}

/*Отступ над кнопками "ЛС, Игнорировать, Публикации" */
.ipsApp .ipsSpacer_top,
.ipsApp .ipsSpacer_both {
    margin-top: 10px;
}

/*Отступы в поле автора поста*/
.cAuthorPane {
    text-align: center;
    padding: 5px !important;
    padding-top: 5px;
    padding-right: 5px;
    padding-bottom: 5px;
    padding-left: 5px;
}

/*Промежутки между постами*/
.ipsComment {
    position: relative;
    margin-bottom: 1px;
}

/*
#ipsLayout_body {
    margin: 0px;
    margin-top: 0px;
    margin-right: 0px;
    margin-bottom: 0px;
    margin-left: 0px;
}*/


/*Боковые отступы*/
.ipsLayout_container {
    max-width: 109%;
    padding: 1px;
    padding-top: 0px;
    padding-right: 0px;
    padding-bottom: 0px;
    padding-left: 0px;
    margin: 0px;
    margin-top: 0px;
    margin-right: 0px;
    margin-bottom: 0px;
    margin-left: 0px;
    position: relative;
}

/*Боковые отступы 2*/
#ipsLayout_mainArea {
    border-radius: 0 !important;
    margin-bottom: 10px;
    padding: 15px;
    padding-top: 15px;
    padding-right: 1px;
    padding-bottom: 15px;
    padding-left: 1px;
}

/*Отступы вокруг тела поста*/
.cPost .cPost_contentWrap {
    position: relative;
    padding-top: 5px;
    padding-right: 15px;
    padding-bottom: 5px;
    padding-left: 0px;
}

/*Отступы вокруг надписи "Опубликовано*/
.cPost .ipsComment_meta {
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
    padding-left: 0px;
}


/*Заголовок страницы*/
.ipsType_pageTitle {
    font-size: 20px;
    font-weight: 400;
    line-height: 1.2;
    margin: 0;
    margin-top: 0px;
    margin-right: 0px;
    margin-bottom: 0px;
    margin-left: 15px;
    color: #000;
}

/*Описание под заголовком страницы*/
.ipsType_richText > p:last-child {
    margin-left: 15px;
}

/*Иконка правой боковой панели*/
.toggle-sidebar {
    right: 0px;

}

/*Бордер вокруг поста*/
.ipsComment, .ipsComment.ipsBox {
    border: 1px solid #c1c1c1;
    border-radius: 5px;
}

/*Бордеры вокруг списка тем в разделе*/
.ipsDataList:not( .ipsGrid ) .ipsDataItem:last-child, .ipsDataList.ipsDataList_zebra > .ipsDataItem {
    border-top: 1px;
    border-top-width: 1px;
    border-top-style: ridge;
    border-top-color: #c1c1c1;
}

/*Размер количества сообщений в разделе на главной странице*/
.ipsDataItem_statsLarge .ipsDataItem_stats_number {
    font-size: 16px;
    font-weight: 300;
    line-height: 1.3;
}
}
