css:
@-moz-document url-prefix("https://www.baidu.com/s") {
/*通用*/
#wrapper_wrapper{
background:#efefef;
}
/*页眉的一些*/
#head{
    box-shadow:0 0 5px #888;
    border-bottom:none;
}
#head{
    background:#3385ff;
}
.head_warpper .c-icon{
    color:#DDD;
}
/*顶栏*/
#s_tab{
    background-color:#1eaaf1;
}
#s_tab b, #s_tab a{
    color:#FFF;
}
/*搜索框*/
.s_ipt_wr.bg{
    background:#FFF;
}
#kw{
    color:#000;
}
.s_ipt_wr:hover, .s_ipt_wr.ipthover{
    /*border-color:rgba(30,170,241,.8);*/
}
/*搜图按钮*/
.soutu-btn{
    background-color:rgba(255,255,255,0);
}
/*搜索按钮包裹*/
.s_ipt_wr{
    border:1px solid #EEE;
}
#result_logo img{
    visibility:hidden;
}
/*搜索按钮*/
.s_btn, .s_btn.btnhover{
    background:#EEE;
    border-bottom:1px solid #DDD;
    color:#000;
}
.soutu-layer .soutu-url-btn, .soutu-layer .soutu-url-btn:hover{
    background:#1eaaf1;
}
/*页眉包裹容器下的链接*/
.head_wrapper #u a{
    color:#FFF;
}
/*搜索结果列*/
#content_left{
    width:560px;
}
/*搜索结果容器*/
.c-container{
    background:#FFF;
    box-shadow: 0px 1px 3px #DDD;
    width:100%;
}
.c-container .c-border{
    border:none;
    box-shadow:none;
}
.c-container .c-row, .c-container .c-gap-top-small, .c-container>.f13, .c-container .c-abstract{
    padding:8px;
}
.t{
    padding:8px;
    background:#DDD;
}
.t a{
    color:#000;
    text-decoration:none;
}
.t em{
    color:#CC0033;
}
/*相关搜索*/
#rs{
    background:none;
}
/*翻页*/
#page{
    background:#1eaaf1;
    padding:8px;
    margin:30px 0 0 121px;/*原大小121px*/
    width:634px;
    box-shadow:0 0 5px #888;
}
#page a, #page strong{
    background:none;
}
#page .pc{
    background:#DDD;
    color:#000;
}
#page .n{
    background:#3385ff;
    color:#FFF;
}
#page .n:hover{
    color:#000;
}
}
