css:
@-moz-document url-prefix("https://www.svt.se/svttext/") {
#logo,
#menu,
#topLine,
span.btnBg,
#linkArea,
.subWrapper {
    display: none;
}
body {
    margin: 0;
}
@media screen and (min-width: 998px) {
    #wrapper {
        zoom: 1.5;
        -moz-transform: scale(1.5);
    }
}
/*@media screen and (min-width: 1330px) {
    #wrapper {
    	zoom: 2;
    	-moz-transform: scale(2);
    }
}*/

#wrapper,
#topNav,
#bottomNav {
    position: relative;
    width: 665px;
    border: 0;
    background: none;
}
#wrapper {
    margin: auto;
}
#topNav,
#bottomNav {
    height: 2em;
    margin: 0 0 0 0;
}
#footerWrapper {
    border-bottom: 0;
}
.btnBg {
    background-color: transparent;
}
}
