css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://delicious.com"),
               url-prefix("https://secure.delicious.com")
{
/****************/
/*****HEADER*****/
/************{{{*/
    #hd
    {
        background: #eee !important;
        border-right: 2px solid #999 !important;
        border-bottom: 2px solid #999 !important;
        border-left: 2px solid #999 !important;
        -moz-border-radius-bottomleft: 3px !important;
        -moz-border-radius-bottomright: 3px !important;
    }
    #ttlbar
    {
        margin-top: 5px !important;
    }
    #ttl
    {
        background: #eee !important;
        border: 2px solid #999 !important;
        -moz-border-radius: 8px !important;
        margin-bottom: 5px !important;
        padding: 3px !important;
    }
    #subnav-list-tags,
    #subnav-list-people,
    #subnav-list-bookmarks,
    #global-searchChoices #global-subnav-list-search
    {
        background: #444 !important;
        border: 1px solid #999 !important;
        -moz-border-radius: 5px !important;
        -moz-opacity: 0.9 !important;
    }
    #actions-list
    {
        background: lightblue !important;
        border: 1px solid #999 !important;
        -moz-border-radius: 5px !important;
        -moz-opacity: 0.9 !important;
        margin-top: 5px !important;
    }
    #actions-list li
    {
        border: none !important;
    }
/*}}}************/
/*****BODY*******/
/************{{{*/
    body{
        background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAIAAAACDbGyAAAAPUlEQVQImU3KoRUAIAgFwM8G8BjFaoPO0lZG8cEIBortwsEthBWAsLoFjdbaADIPucWo+gorCetoCv25+j6u1xVXfiv0FQAAAABJRU5ErkJggg==") fixed !important;
    }
/************************************************************/
    #bd
    {
        background: #585D60 !important;
        border: 2px solid #999 !important;
        -moz-border-radius: 8px !important;
    }
    #bd dt a,
    #bd p a,
    #display-and-rss-options a
    {
        color: skyblue !important;
    }
    #bd h1,
    #bd h3,
    #bd h4
    {
        color: #333 !important;
    }
    #bd dd,
    #bd li,
    #bd p
    {
        color: #DDD !important;
    }
/************************************************************/
    #searchbar
    {
        background: #DDD !important;
    }
    #searchbar h3
    {
        background: #FFF !important;
        -moz-border-radius: 8px !important;
    }
    .dateGroup
    {
        color     : #ddd !important; 
        border    : none !important; 
    }
/************************************************************/
    #magicboxform .yui-ac-input
    {
        height: 13px !important;
        border: 1px solid #999 !important;
        -moz-border-radius: 5px !important;
        padding: 0 !important;
    }
    #magicboxform .yui-ac-input:focus
    {
        background: #777 !important;
        border: 1px solid orange !important;
    }
    #magicboxform .yui-ac-content
    {
        background: #444 !important;
        border: 1px solid #999 !important;
        -moz-border-radius: 5px !important;
        -moz-opacity: 0.9 !important;
    }
    #magicboxform .yui-ac-content .yui-ac-bd li
    {
        color: #EEE !important;
    }
    #magicboxform .yui-ac-content .yui-ac-bd li:hover
    {
        color: #222 !important;
        background: #eee !important;
    }
    #viewact-container
    {
        background: #DDD !important;
        border: 1px solid #FFF !important;
        -moz-border-radius: 2px !important;
    }
/************************************************************/
    #displayOptions ul
    {
        border: 1px solid #999 !important;
        -moz-border-radius: 5px !important;
        -moz-opacity: 0.9 !important;
    }
    #yourNotes,
    #yourNotes .bookmarks .isSelf
    {
        background: #444 !important;
        border: none !important;
    }
/************************************************************/
    .post
    {
        border: none !important;
    }
    .bookmark
    {
        background: #aaa url("data:image/png;base64,") repeat-x !important;
        margin         : 8px            !important; 
        border: 2px solid #999 !important;
        -moz-border-radius: 8px !important;
    }
    .bookmarks a.taggedlink
    {
        color: #003344 !important; 
        padding-left: 10px    !important; 
    }
/************************************************************/
    #pagination a
    {
        -moz-border-radius: 5px !important;
    }
    #ipplistoptions
    {
        background: #555 !important;
        -moz-border-radius: 3px !important;
        -moz-opacity: 0.8 !important;
    }
    #ipplistoptions .ippoption-item
    {
        border: none !important;
    }
/*}}}************/
/*****SIDEBAR****/
/************{{{*/
    #sidebar
    {
        background: #F9F9F9 url("data:image/png;base64,") repeat-y !important;
        border     : 2px solid #999 !important; 
        margin: 3px !important;
        -moz-border-radius: 8px !important;
    }
    #sidebar p
    {
        color: #444 !important;
    }
/*}}}************/
/*****TAG********/
/************{{{*/
    .size5
    {
        color: yellow !important;
    }
    .size4
    {
        color: lightblue !important;
    }
    .size3
    {
        color: lightgreen !important;
    }
    .size2
    {
        color: pink !important;
    }
    .size1
    {
        color: orange !important;
    }
    .size0
    {
        color: lightgray !important;
    }
    li[class="0"],
    li[class="1"],
    li[class="2"],
    li[class="3"],
    li[class="4"],
    li[class="5"],
    .size0,
    .size1,
    .size2,
    .size3,
    .size4,
    .size5
    {
        -moz-border-radius: 3px !important;
    }
    li[class="0"]:hover,
    li[class="1"]:hover,
    li[class="2"]:hover,
    li[class="3"]:hover,
    li[class="4"]:hover,
    li[class="5"]:hover,
    .size0:hover,
    .size1:hover,
    .size2:hover,
    .size3:hover,
    .size4:hover,
    .size5:hover
    {
        background: #000 !important;
    }
    .common0,
    .common1,
    .common2,
    .common3,
    .common4,
    .common5
    {
        background: #444 !important;
        -moz-border-radius: 3px !important;
        -moz-opacity: 0.8 !important;
        padding: 1px !important;
    }
/*}}}*/
}
@-moz-document url-prefix("http://delicious.com/help")
{
    #bd li a
    {
        color: orange !important;
    }
}
