css:
@-moz-document url-prefix("http://sao.gamesprite.me/") {
body, a {
    color: white !important;
}
.topbox {
    background: url(http://cdn.gamesprite.me/style/official/single_game/saoslegend/es/img/bg.jpg?20170329) center center !important;
    color: white !important;
}
.top_center, .topright .user_info span {
    color: white !important; font-family: "Arial Black", Charcoal, sans-serif !important;
}
.languageWrap, .topleft a.all, .playTipBg, a#recharge {
    display: none !important;
}
.topright .user_info .logout {
    background: url(http://www.swordart-online.net/img/global_top/on/pattern.png) #000 !important; border: none !important;
}
.play_footer, .play_footer_leftside {
    background: #000 !important;
}
.play_footer_leftside ul.playNav li a, .email_yanzheng span.V_email, .forget_right p strong, .forget_right div a.passwBtn, .forget_right p, .emailInfoWrap h1, .messageClose, .emailInfoBox h2, strong, b, .login_username p span.login_history, ul.nav li a, .Select_number, .Select_button a.active, .Select_button a, .newscon_info h3 a, .newscon_info p a {
    font-weight: normal !important;
}
.cont {
    background: url(http://dumpshare.net/images/1645810new_titlebg1.png) 0 0 no-repeat !important;
}
.froum_btn {
    background: url(http://dumpshare.net/images/4229848forum_btn.png) 0 0 no-repeat !important;
}
.forget_left {
    background: #000 url(http://www.swordart-online.net/img/left_common/bg/bg_num.gif) no-repeat center center !important;
    border-right: 1px solid #fff !important;
}
.forget_right, .forget_password {
    background: #000000 !important;
}
.forget_left li {
    font-weight: normal !important;
    border-bottom: none !important;
    background: url(http://www.swordart-online.net/img/left_common/dot/dot.png) no-repeat !important;
}
.forget_left li.red {
    background: url(http://www.swordart-online.net/img/global_top/icon_direct2.gif) no-repeat right !important;
}
.forget_right h2 {
    background: none;
}
}
