css:
@-moz-document domain("www.flashback.org") {
/**
* Flashback 2017 - Optimize Legability
* Changes font sizes and uses max 80 chars/line for optimized legability (readability)
*/

body, #site .page-title a {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
    font-size: 1.3rem;
}

/*
	UI font stack : change global font
	Also temp-back to remove 11px(!) Verdana ...
*/
body {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
    font-size: 1.4rem !important;
}

body, #site .page-title a, html > body, .post_message, #site td, #site caption {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
}

#site .post .post-body .post-right {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
    font-size: 1.4rem;
}

#threadslist td, #site-left td a { font-size: 1.4rem; }
#site .thread-forum-title, #site .text-right { font-size: 1.3rem; }
#site .smallfont { font-size: 1.2rem; }

#site .post-user-info.small { font-size: 1.2rem !important; }
#site .forumslist strong { font-size: 1.4rem !important; }
.table-threads>thead>tr>th, .tcat{ font-size:1.4rem }
#footer a, #footer { font-weight: 400; }
#site .post-user-username { font-size: 1.4rem; text-decoration: none; }

/* remove all crap */
div[class*="banner"],
[id*="banner"],
#site-right
{ display: none; }

#site-left { width: 100%; }
#top, #site-content { max-width: 850px; }
#top { margin-top: 40px; }

/* static nav */
body { padding-top:0; }
#navbar-top, #navbar-forumnav {
    position: relative;
    top:0; margin-top:0;
}

/* site bg */
#site, body  {
	background: #eee;
}

/* fix post text */
#posts { background: #fff; }
#site .post_message { line-height: 1.5; }
#site .post-body { margin-bottom: 20px; }

.post_message {
    font-size: 1.4rem;
    padding: 0 10px 20px 0;
    line-height: 1.5rem;
}

#site .post-row .post-col, #site .post {
    background: #fff;
    border: 0;
}

#site .post-heading {
	margin-bottom: 10px;
    background: #eee;
}

#site .post-icons {
    border-top: 1px solid #ddd !important;
}

#site .post-bbcode-quote-wrapper {
    box-sizing: border-box;
    width: 100% !important;
    font-size: 1.4rem; line-height: 1.45;
    margin: 0 0 20px 0 !important;
}


#site .post-bbcode-quote {
     padding: 15px;
     background: #eee;
     border-color: #ddd;
}

/* Small fixes */
#site b, #site strong, #site .thread-title {
	font-weight: 600 !important;
}

#site .td_title a,
#site .forumslist a
{ text-decoration: none; }

.tooltip { display: none !important; }
}
